import React, { useEffect, useState } from "react";
import axios from "axios";
import ProfileEditModal from "../components/Profile/ProfileEditModal";

const ProfilePage = ({ user }) => {
  const [favorites, setFavorites] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);


  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
  const token = localStorage.getItem("token");

  // Favori tarifleri getir
  useEffect(() => {
    if (!user?.userId || !token) return;

    const fetchFavorites = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/Favorite/GetFavoritesByUserId/${user.userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFavorites(res.data || []);
      } catch (err) {
        console.error("Favoriler yüklenemedi:", err);
      }
    };

    fetchFavorites();
  }, [user]);

  // Kullanıcının yaptığı yorumları getir
  useEffect(() => {
    if (!user?.userId) return;

    const fetchReviews = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/Review/GetReviewsByUserId/${user.userId}`);
        setReviews(res.data || []);
      } catch (err) {
        console.error("Yorumlar yüklenemedi:", err);
      }
    };

    fetchReviews();
  }, [user]);

  return (
    <div className="profile-page">
      <h2>Profil Sayfası</h2>

      {/* 3️⃣ Modal Bileşeni (koşullu render) */}
      {showEditModal && (
        <ProfileEditModal
          user={user}
          onClose={() => {
            setShowEditModal(false);
            window.location.reload(); // Güncel bilgileri almak için sayfayı yeniler
          }}
        />
      )}

      <section>
        <h3>👤 Bilgiler</h3>
        


        <p><strong>Kullanıcı Adı:</strong> {user?.username}</p>
        <p><strong>Email:</strong> {user?.email}</p>
        <button onClick={() => setIsEditModalOpen(true)}>Profili Düzenle</button>
      </section>

      {isEditModalOpen && (
        <ProfileEditModal user={user} onClose={() => setIsEditModalOpen(false)} />
      )}

      <section>
        <h3>❤️ Favori Tarifler</h3>
        {favorites.length === 0 ? (
          <p>Favori tarif bulunamadı.</p>
        ) : (
          <ul>
            {favorites.map((fav, i) => (
              <li key={i}>
                {fav.recipeName}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h3>💬 Yaptığınız Yorumlar</h3>
        {reviews.length === 0 ? (
          <p>Henüz yorum yapmadınız.</p>
        ) : (
          <ul>
            {reviews.map((rev, i) => (
              <li key={i}>
                <strong>{rev.recipeName}</strong>: {rev.comment} ⭐{rev.rating}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default ProfilePage;
