                matchesCategory = normalizedCategory === normalizedSelected || 
                                 normalizedCategory.includes(normalizedSelected) ||
                                 normalizedSelected.includes(normalizedCategory);
              }

              // ðŸ” Konsola yazalÄ±m
              console.log("Tarif:", title);
              console.log("VeritabanÄ± Kategorisi:", category);
              console.log("SeÃ§ili Kategori:", selectedCategory);
              console.log("EÅŸleÅŸiyor mu:", matchesCategory);
              console.log("---");

              return matchesSearch && matchesCategory;
            })
            .map(({ id, attributes }) => (
              <RecipeCard
