import React, { useState, useEffect } from "react";
import "./ProfilePage.css";
import { reviewService } from "../../services/reviewService";
import { userService } from "../../services/userService";
import { recipeService } from "../../services/recipeService";
import { getProfilePhotoUrl } from "../../services/api";
import RecipeCard from "../Recipe/RecipeCard";
import RecipeDetailModal from "../Recipe/RecipeDetailModal";
import ProfileEditModal from "./ProfileEditModal";

const tabs = [
  { key: "profile", label: "Profil" },
  { key: "favorites", label: "Favoriler" },
  { key: "comments", label: "YorumlarÄ±m" },
];

export default function ProfilePage({ user, favorites, recipes, onFavoriteClick, onProfileUpdate }) {
  const [activeTab, setActiveTab] = useState("profile");
  const [userComments, setUserComments] = useState([]);
  const [loadingComments, setLoadingComments] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [showRecipeModal, setShowRecipeModal] = useState(false);
  const [showProfileEditModal, setShowProfileEditModal] = useState(false);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [userProfile, setUserProfile] = useState(null);
  const [loadingProfile, setLoadingProfile] = useState(true);

  useEffect(() => {
    if (user?.userId) {
      // Ã–nceki profil verilerini temizle
      setUserProfile(null);
      setProfilePhoto(null);
      setUserComments([]);
      
      // KÄ±sa bir gecikme ile profil yÃ¼kle
      const timer = setTimeout(() => {
        loadUserProfile();
        loadUserComments();
      }, 100);
      
      return () => clearTimeout(timer);
    } else {
      // KullanÄ±cÄ± yoksa profil bilgilerini temizle
      setUserProfile(null);
      setProfilePhoto(null);
      setUserComments([]);
    }
  }, [user?.userId, user?.username]); // Hem userId hem de username deÄŸiÅŸikliklerini izle

  useEffect(() => {
    console.log("ProfilePage - Profil fotoÄŸrafÄ± ayarlanÄ±yor:", {
      userProfile: userProfile,
      profileImageBase64: userProfile?.profileImageBase64,
      userId: user?.userId,
      username: user?.username
    });
    
    if (userProfile?.profileImageBase64 && user?.userId) {
      // Base64 formatÄ±nda ise doÄŸrudan kullan
      if (userProfile.profileImageBase64.startsWith('data:image/')) {
        console.log("ProfilePage - Base64 formatÄ±nda profil fotoÄŸrafÄ± ayarlanÄ±yor");
        setProfilePhoto(userProfile.profileImageBase64);
      } else {
        // Dosya yolu formatÄ±nda ise URL oluÅŸtur
        const photoUrl = getProfilePhotoUrl(userProfile.profileImageBase64);
        // Benzersiz cache busting
        const uniqueId = `${user.userId}_${Date.now()}_${Math.random()}`;
        const cacheBustUrl = `${photoUrl}?uid=${uniqueId}`;
        console.log("ProfilePage - URL formatÄ±nda profil fotoÄŸrafÄ± ayarlanÄ±yor:", {
          originalUrl: photoUrl,
          cacheBustUrl: cacheBustUrl,
          userId: user.userId
        });
        setProfilePhoto(cacheBustUrl);
      }
    } else {
      // Profil fotoÄŸrafÄ± yoksa temizle
      console.log("ProfilePage - Profil fotoÄŸrafÄ± temizleniyor");
      setProfilePhoto(null);
    }
  }, [userProfile?.profileImageBase64, user?.userId]);

  const loadUserProfile = async () => {
    try {
      // KullanÄ±cÄ± kontrolÃ¼
      if (!user?.userId) {
        console.log("ProfilePage - KullanÄ±cÄ± ID yok, profil yÃ¼kleme iptal edildi");
        return;
      }
      
      setLoadingProfile(true);
      console.log("ProfilePage - Profil yÃ¼kleniyor, userId:", user.userId, "username:", user.username);
      
      // Profil var mÄ± kontrol et
      const existsResponse = await userService.profileExists(user.userId);
      
      if (existsResponse.data) {
        // Profil varsa getir
        const response = await userService.getUserProfile(user.userId);
        if (response.data) {
          console.log("ProfilePage - Profil yÃ¼klendi:", {
            profileData: response.data,
            profileImageBase64: response.data.profileImageBase64,
            userId: response.data.userId,
            expectedUserId: user.userId
          });
          // KullanÄ±cÄ± kontrolÃ¼ yap
          if (response.data.userId === user.userId) {
            setUserProfile(response.data);
          } else {
            console.log("ProfilePage - Profil userId eÅŸleÅŸmiyor, yeni profil oluÅŸturuluyor");
            await createUserProfile();
          }
        }
      } else {
        // Profil yoksa oluÅŸtur
        console.log("ProfilePage - Profil bulunamadÄ±, yeni profil oluÅŸturuluyor");
        await createUserProfile();
      }
    } catch (error) {
      console.error("ProfilePage - Profil yÃ¼kleme hatasÄ±:", error);
      // Hata durumunda yeni profil oluÅŸturmayÄ± dene
      try {
        await createUserProfile();
      } catch (createError) {
        console.error("ProfilePage - Profil oluÅŸturma hatasÄ±:", createError);
      }
    } finally {
      setLoadingProfile(false);
    }
  };

  // Yeni profil oluÅŸtur
  const createUserProfile = async () => {
    try {
             const newProfile = {
         userId: user.userId,
         username: user.username,
         email: user.email,
         profileImageBase64: null,
         bio: null,
         location: null,
         website: null,
         dateOfBirth: null,
         phoneNumber: null
       };
      
      const response = await userService.createUserProfile(newProfile);
      if (response.data) {
        setUserProfile(response.data);
      }
    } catch (error) {
      console.error("Profil oluÅŸturma hatasÄ±:", error);
    }
  };

  // KullanÄ±cÄ± yorumlarÄ±nÄ± yÃ¼kle
  const loadUserComments = async () => {
    try {
      setLoadingComments(true);
      console.log("Yorumlar yÃ¼kleniyor, userId:", user.userId);
      console.log("Mevcut recipes array:", recipes);
      
      // TÃ¼m yorumlarÄ± al
      const response = await reviewService.getReviews();
      console.log("TÃ¼m yorumlar alÄ±ndÄ±:", response.data);
      
      const userReviews = response.data.filter(review => review.userId === user.userId);
      console.log("KullanÄ±cÄ±nÄ±n yorumlarÄ±:", userReviews);
      
      // recipeId 0 olan yorumlarÄ± filtrele (geÃ§ersiz yorumlar)
      const validReviews = userReviews.filter(review => review.recipeId && review.recipeId !== 0);
      console.log("GeÃ§erli yorumlar (recipeId > 0):", validReviews);
      console.log("Filtrelenen yorumlar (recipeId = 0):", userReviews.filter(review => !review.recipeId || review.recipeId === 0));
      
      // Ã–nce mevcut recipes array'inden tarif bilgilerini bul
      const reviewsWithRecipes = validReviews.map((review) => {
        console.log(`Tarif ${review.recipeId} iÃ§in mevcut recipes array'inde aranÄ±yor...`);
        
        // Mevcut recipes array'inden kontrol et
        const existingRecipe = recipes.find(r => r.recipeId === review.recipeId);
        if (existingRecipe) {
          console.log(`Tarif ${review.recipeId} mevcut recipes array'inde bulundu:`, existingRecipe.recipeName);
          return {
            ...review,
            recipeName: existingRecipe.recipeName
          };
        }
        
        console.log(`Tarif ${review.recipeId} mevcut recipes array'inde bulunamadÄ±, API'den Ã§ekiliyor...`);
        
        // Mevcut recipes array'inde yoksa API'den Ã§ek
        return {
          ...review,
          recipeName: "API'den yÃ¼kleniyor..."
        };
      });
      
      // API'den yÃ¼klenmesi gereken tarifler iÃ§in ayrÄ± iÅŸlem yap
      const reviewsNeedingAPI = reviewsWithRecipes.filter(review => review.recipeName === "API'den yÃ¼kleniyor...");
      
      if (reviewsNeedingAPI.length > 0) {
        console.log(`${reviewsNeedingAPI.length} tarif API'den yÃ¼klenecek`);
        
        const apiLoadedReviews = await Promise.all(
          reviewsNeedingAPI.map(async (review) => {
            try {
              const recipeResponse = await recipeService.getRecipeById(review.recipeId);
              console.log(`Tarif ${review.recipeId} API'den yÃ¼klendi:`, recipeResponse.data?.recipeName);
              
              if (recipeResponse.data && recipeResponse.data.recipeName) {
                return {
                  ...review,
                  recipeName: recipeResponse.data.recipeName
                };
              } else {
                console.error(`Tarif ${review.recipeId} iÃ§in recipeName bulunamadÄ±:`, recipeResponse.data);
                return {
                  ...review,
                  recipeName: "Tarif bilgisi eksik"
                };
              }
            } catch (recipeError) {
              console.error(`Tarif ${review.recipeId} API'den yÃ¼klenemedi:`, recipeError);
              return {
                ...review,
                recipeName: "Tarif silinmiÅŸ"
              };
            }
          })
        );
        
        // API'den yÃ¼klenen tarifleri gÃ¼ncelle
        const finalReviews = reviewsWithRecipes.map(review => {
          if (review.recipeName === "API'den yÃ¼kleniyor...") {
            const apiLoadedReview = apiLoadedReviews.find(r => r.reviewId === review.reviewId);
            return apiLoadedReview || { ...review, recipeName: "Tarif silinmiÅŸ" };
          }
          return review;
        });
        
        console.log("Final yorumlar:", finalReviews);
        setUserComments(finalReviews);
      } else {
        console.log("TÃ¼m tarifler mevcut recipes array'inden bulundu");
        setUserComments(reviewsWithRecipes);
      }
      
    } catch (error) {
      console.error("Yorumlar yÃ¼klenemedi:", error);
      setUserComments([]);
    } finally {
      setLoadingComments(false);
    }
  };

  // Profil fotoÄŸrafÄ± gÃ¼ncelleme
  const handleProfilePhotoUpdate = async (photoUrl) => {
    if (userProfile) {
      try {
        const updatedProfile = {
          ...userProfile,
          profileImageBase64: photoUrl // Backend'deki property adÄ±na uygun
        };
        
        const response = await userService.updateUserProfile(updatedProfile);
        if (response.data) {
          setUserProfile(response.data);
          // Navbar'Ä± gÃ¼ncellemek iÃ§in callback Ã§aÄŸÄ±r
          if (onProfileUpdate) {
            onProfileUpdate();
          }
        }
      } catch (error) {
        console.error("Profil gÃ¼ncelleme hatasÄ±:", error);
      }
    }
  };

  // Tarif detayÄ± aÃ§
  const handleRecipeClick = (recipe) => {
    setSelectedRecipe(recipe);
    setShowRecipeModal(true);
  };

  // Modal kapat
  const handleCloseModal = () => {
    setShowRecipeModal(false);
    setSelectedRecipe(null);
  };

  // Favori tarifleri filtrele
  const favoriteRecipes = recipes.filter((r) => favorites && favorites.includes(r.recipeId));
  
  // Debug bilgileri
  console.log("ProfilePage Debug:", {
    userId: user?.userId,
    favorites: favorites,
    favoritesType: Array.isArray(favorites) ? 'array' : typeof favorites,
    recipesCount: recipes?.length,
    favoriteRecipesCount: favoriteRecipes?.length,
    userCommentsCount: userComments?.length,
    recipes: recipes?.slice(0, 3) // Ä°lk 3 tarifi gÃ¶ster
  });

  return (
    <div className="profile-root">
      {/* Profil BaÅŸlÄ±ÄŸÄ± */}
      <div className="profile-cover">
        <button className="profile-photo-btn" onClick={() => setShowProfileEditModal(true)}>
          ğŸ“· Profili DÃ¼zenle
        </button>
      </div>

      {/* Profil Ana Bilgileri */}
      <div className="profile-main">
        <div className="profile-avatar">
                                           {profilePhoto ? (
              <img 
                src={profilePhoto} 
                alt="Profil fotoÄŸrafÄ±" 
                className="profile-photo"
                key={`${user?.userId}-${Date.now()}-${Math.random()}`}
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'block';
                }}
              />
            ) : null}
          <span role="img" aria-label="avatar" style={{ fontSize: 64, display: profilePhoto ? 'none' : 'block' }}>
            ğŸ‘¤
          </span>
        </div>
        <div className="profile-info">
          <div className="profile-name">{user?.username || "KullanÄ±cÄ±"}</div>
          <div className="profile-username">@{user?.username || "kullanici"}</div>
        </div>
        <button className="profile-edit-btn" onClick={() => setShowProfileEditModal(true)}>
          Profili DÃ¼zenle
        </button>
      </div>

      {/* Tab MenÃ¼sÃ¼ */}
      <div className="profile-tabs">
        {tabs.map((tab) => (
          <button
            key={tab.key}
            className={activeTab === tab.key ? "profile-tab active" : "profile-tab"}
            onClick={() => setActiveTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Tab Ä°Ã§erikleri */}
      <div className="profile-tab-content">
        {/* Profil Tab */}
        {activeTab === "profile" && (
          <div>
            {loadingProfile ? (
              <div style={{ textAlign: 'center', padding: '20px' }}>
                <p>Profil bilgileri yÃ¼kleniyor...</p>
              </div>
            ) : (
              <>
                <h3>HoÅŸ geldin, {userProfile?.username || user?.username || "KullanÄ±cÄ±"}!</h3>
                <p>Burada profil bilgilerini ve ayarlarÄ±nÄ± gÃ¶rebilirsin.</p>
                
                <div className="profile-details" style={{ marginTop: 20 }}>
                  <div className="profile-detail-item">
                    <strong>KullanÄ±cÄ± AdÄ±:</strong> {userProfile?.username || user?.username || "BelirtilmemiÅŸ"}
                  </div>
                  <div className="profile-detail-item">
                    <strong>E-posta:</strong> {userProfile?.email || user?.email || "BelirtilmemiÅŸ"}
                  </div>
                  {userProfile?.bio && (
                    <div className="profile-detail-item">
                      <strong>HakkÄ±mda:</strong> {userProfile.bio}
                    </div>
                  )}
                  {userProfile?.location && (
                    <div className="profile-detail-item">
                      <strong>Konum:</strong> {userProfile.location}
                    </div>
                  )}
                  {userProfile?.website && (
                    <div className="profile-detail-item">
                      <strong>Website:</strong> 
                      <a href={userProfile.website} target="_blank" rel="noopener noreferrer" style={{ marginLeft: 5, color: '#007bff' }}>
                        {userProfile.website}
                      </a>
                    </div>
                  )}
                  {userProfile?.phoneNumber && (
                    <div className="profile-detail-item">
                      <strong>Telefon:</strong> {userProfile.phoneNumber}
                    </div>
                  )}
                  {userProfile?.dateOfBirth && (
                    <div className="profile-detail-item">
                      <strong>DoÄŸum Tarihi:</strong> {new Date(userProfile.dateOfBirth).toLocaleDateString('tr-TR')}
                    </div>
                  )}
                  {userProfile?.createdAt && (
                    <div className="profile-detail-item">
                      <strong>Ãœyelik Tarihi:</strong> {new Date(userProfile.createdAt).toLocaleDateString('tr-TR')}
                    </div>
                  )}
                </div>
              </>
            )}
          </div>
        )}

        {/* Favoriler Tab */}
        {activeTab === "favorites" && (
          <div>
            <h3>Favori Tariflerin ({favoriteRecipes.length})</h3>
            <div className="recipe-grid">
              {favoriteRecipes.length === 0 ? (
                <div>HenÃ¼z favori tarifin yok.</div>
              ) : (
                favoriteRecipes.map((r) => (
                  <RecipeCard
                    key={r.recipeId}
                    title={r.recipeName}
                    imageUrl={r.recipeImageUrl}
                    calories={r.calories}
                    prepTime={r.prepTime}
                    ingredients={r.ingredients}
                    recipeInstructions={r.recipeInstructions}
                    isVegetarian={r.isVegetarian}
                    onClick={() => handleRecipeClick(r)}
                    isFavorite={favorites.includes(r.recipeId)}
                    onFavoriteClick={() => onFavoriteClick(r.recipeId)}
                    user={user}
                  />
                ))
              )}
            </div>
          </div>
        )}

        {/* Yorumlar Tab */}
        {activeTab === "comments" && (
          <div>
            <h3>YorumlarÄ±m ({userComments.length})</h3>
            <div style={{ marginBottom: 10, fontSize: '14px', color: '#666' }}>
              <p>KullanÄ±cÄ± ID: {user?.userId}</p>
              <p>YÃ¼kleme durumu: {loadingComments ? 'YÃ¼kleniyor' : 'TamamlandÄ±'}</p>
              <p>Mevcut tarif sayÄ±sÄ±: {recipes?.length || 0}</p>
              <p>Toplam yorum sayÄ±sÄ±: {userComments.length}</p>
              <p>GeÃ§erli yorum sayÄ±sÄ±: {userComments.filter(c => c.recipeId && c.recipeId !== 0).length}</p>
              <p>GeÃ§ersiz yorum sayÄ±sÄ±: {userComments.filter(c => !c.recipeId || c.recipeId === 0).length}</p>
              <p>Yorum detaylarÄ±:</p>
              {userComments.map((comment, index) => (
                <div key={index} style={{ fontSize: '12px', marginLeft: 10 }}>
                  Yorum {index + 1}: ID={comment.reviewId}, Tarif ID={comment.recipeId}, Tarif AdÄ±="{comment.recipeName}"
                </div>
              ))}
            </div>
            {loadingComments ? (
              <p>Yorumlar yÃ¼kleniyor...</p>
            ) : userComments.length === 0 ? (
              <p>HenÃ¼z yorum yapmadÄ±nÄ±z.</p>
            ) : (
              <ul style={{ listStyle: "none", paddingLeft: 0 }}>
                {userComments.map((comment) => {
                  // Yeni API'den gelen veri yapÄ±sÄ±na gÃ¶re tarif adÄ±nÄ± al
                  const recipeName = comment.recipeName || comment.recipe?.recipeName || "Tarif silinmiÅŸ";

                  return (
                    <li
                      key={comment.reviewId}
                      style={{ marginBottom: 12, borderBottom: "1px solid #ccc", paddingBottom: 8 }}
                    >
                      <strong>{recipeName}</strong>
                      <p>{comment.reviewText}</p>
                      <small>â­ {typeof comment.rating === "number" ? `${comment.rating} / 5` : "Puan yok"}</small>
                      <small style={{ display: 'block', fontSize: '10px', color: '#999' }}>
                        Yorum ID: {comment.reviewId}, Tarif ID: {comment.recipeId}, RecipeName: "{comment.recipeName}"
                      </small>
                    </li>
                  );
                })}
              </ul>
            )}
          </div>
        )}
      </div>
      
      {/* Modaller */}
      {showRecipeModal && selectedRecipe && (
        <RecipeDetailModal
          recipe={selectedRecipe}
          user={user}
          onClose={handleCloseModal}
        />
      )}

      <ProfileEditModal
        user={user}
        userProfile={userProfile}
        isOpen={showProfileEditModal}
        onClose={() => setShowProfileEditModal(false)}
        onPhotoUpdate={handleProfilePhotoUpdate}
      />
    </div>
  );
}
