import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./components/Navbar";
import RecipeCard from "./components/RecipeCard";
import "./App.css";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("T√ºm√º");
  const [categories, setCategories] = useState(["T√ºm√º"]);
  const [userIngredients, setUserIngredients] = useState([]);
  const [inputIngredient, setInputIngredient] = useState("");
  const [showOnlyPossible, setShowOnlyPossible] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/tarifs?populate=*")
      .then((res) => {
        setRecipes(res.data.data);
        console.log("Gelen tarifler:", res.data.data);

        // Veritabanƒ±ndan gelen kategorileri al
        const uniqueCategories = [...new Set(
          res.data.data
            .map(recipe => recipe.attributes.category)
            .filter(category => category && category.trim() !== "")
        )];

        setCategories(["T√ºm√º", ...uniqueCategories]);
        console.log("Bulunan kategoriler:", uniqueCategories);
      })
      .catch((err) => console.error("Veri alƒ±namadƒ±:", err));
  }, []);

  return (
    <div>
      <Navbar
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        categories={categories}
        onLoginClick={() => setShowLogin(true)}
        onRegisterClick={() => setShowRegister(true)}
      />
      <div style={{ maxWidth: 500, margin: "24px auto 0", background: "#fff", borderRadius: 12, padding: 16, boxShadow: "0 2px 8px rgba(0,0,0,0.04)" }}>
        <h3>Elinizdeki Malzemeler</h3>
        <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
          <input
            type="text"
            value={inputIngredient}
            onChange={e => setInputIngredient(e.target.value)}
            placeholder="Malzeme ekle..."
            style={{ flex: 1, padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
            onKeyDown={e => { if (e.key === "Enter" && inputIngredient.trim()) { setUserIngredients([...userIngredients, inputIngredient.trim().toLowerCase()]); setInputIngredient(""); } }}
          />
          <button
            onClick={() => { if (inputIngredient.trim()) { setUserIngredients([...userIngredients, inputIngredient.trim().toLowerCase()]); setInputIngredient(""); } }}
            style={{ padding: "8px 16px", borderRadius: 6, background: "#e60023", color: "#fff", border: "none" }}
          >Ekle</button>
        </div>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginBottom: 12 }}>
          {userIngredients.map((ing, idx) => (
            <span key={idx} style={{ background: "#ffe0e0", color: "#e60023", padding: "4px 10px", borderRadius: 8, fontSize: 15 }}>{ing} <button style={{ background: "none", border: "none", color: "#e60023", cursor: "pointer" }} onClick={() => setUserIngredients(userIngredients.filter((_, i) => i !== idx))}>√ó</button></span>
          ))}
        </div>
        <button
          onClick={() => setShowOnlyPossible(!showOnlyPossible)}
          style={{ padding: "8px 16px", borderRadius: 6, background: showOnlyPossible ? "#333" : "#e60023", color: "#fff", border: "none", marginTop: 8 }}
        >{showOnlyPossible ? "T√ºm Tarifleri G√∂ster" : "Sadece Yapabileceƒüim Tarifler"}</button>
      </div>
      <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
        <div className="recipe-grid">
        {recipes
  .filter((r) => {
    if (!r) return false;
    const title = r.title?.toLowerCase() || "";
    const category = r.category || "";
    const search = searchTerm.toLowerCase();
    const matchesSearch =
      title.includes(search) ||
      category.toLowerCase().includes(search);
    const matchesCategory =
      selectedCategory === "T√ºm√º" ||
      (category && category.trim().toLowerCase() === selectedCategory.trim().toLowerCase());

    // Malzeme bazlƒ± filtreleme: Kullanƒ±cƒ±nƒ±n girdiƒüi malzemelerden en az biri tarifteki herhangi bir malzemenin i√ßinde ge√ßiyorsa g√∂ster
    let matchesIngredients = true;
    if (userIngredients.length > 0) {
      let recipeIngredients = [];
      if (typeof r.ingredients === "string") {
        recipeIngredients = r.ingredients.split(/,|\n/).map(i => i.trim().toLowerCase()).filter(Boolean);
      } else if (Array.isArray(r.ingredients)) {
        recipeIngredients = r.ingredients.map(i => i.trim().toLowerCase());
      }
      matchesIngredients = userIngredients.some(userIng =>
        recipeIngredients.some(recipeIng => recipeIng.includes(userIng))
      );
    }
    return matchesSearch && matchesCategory && matchesIngredients;
  })
  .map((r) => {
    const { id, title, image, calories, ingredients, prep_time, is_vegetarian } = r;
    // G√∂rsel url'si i√ßin:
    let imageUrl = "";
    if (Array.isArray(image) && image.length > 0) {
      imageUrl = "http://localhost:1337" + (image[0].formats?.medium?.url || image[0].url);
    }
    // Eksik malzemeleri bul
    let missingIngredients = [];
    let recipeIngredients = [];
    if (typeof ingredients === "string") {
      recipeIngredients = ingredients.split(/,|\n/).map(i => i.trim().toLowerCase()).filter(Boolean);
    } else if (Array.isArray(ingredients)) {
      recipeIngredients = ingredients.map(i => i.trim().toLowerCase());
    }
    missingIngredients = recipeIngredients.filter(ing => !userIngredients.includes(ing));
    return (
      <RecipeCard
        key={id}
        title={title}
        imageUrl={imageUrl}
        calories={calories}
        ingredients={ingredients}
        prepTime={prep_time}
        isVegetarian={is_vegetarian}
        missingIngredients={missingIngredients}
        userIngredients={userIngredients}
        onClick={() => setSelectedRecipe(r)}
        showIngredients={false}
      />
    );
  })
}
        </div>
      </div>
      {selectedRecipe && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 1000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={() => setSelectedRecipe(null)}>
          <div style={{ background: "#fff", borderRadius: 16, padding: 32, maxWidth: 600, width: "100%", position: "relative", maxHeight: "90vh", overflowY: "auto" }} onClick={e => e.stopPropagation()}>
            <button onClick={() => setSelectedRecipe(null)} style={{ position: "absolute", top: 16, right: 16, background: "#e60023", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
            <img src={(() => { let imageUrl = ""; const image = selectedRecipe.image; if (Array.isArray(image) && image.length > 0) { imageUrl = "http://localhost:1337" + (image[0].formats?.medium?.url || image[0].url); } return imageUrl; })()} alt={selectedRecipe.title} style={{ width: "100%", borderRadius: 12, marginBottom: 16 }} />
            <h2>{selectedRecipe.title}</h2>
            <div style={{ color: "#e60023", marginBottom: 8 }}>
              <span>üî• {selectedRecipe.calories} kalori</span> | <span>‚è±Ô∏è {selectedRecipe.prep_time} dk</span> {selectedRecipe.is_vegetarian ? "| üå± Vejetaryen" : ""}
            </div>
            <p style={{ marginBottom: 16 }}>{selectedRecipe.description}</p>
            <div>
              <strong>Malzemeler:</strong>
              <ul>
                {(typeof selectedRecipe.ingredients === "string"
                  ? selectedRecipe.ingredients.split(/,|\n/).map(i => i.trim()).filter(Boolean)
                  : Array.isArray(selectedRecipe.ingredients)
                  ? selectedRecipe.ingredients
                  : []
                ).map((item, idx) => (
                  <li key={idx} style={{ marginBottom: 4 }}>
                    {item}
                    <a
                      href={`https://www.amazon.com.tr/s?k=${encodeURIComponent(item)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="amazon-btn"
                      style={{ marginLeft: 8 }}
                    >
                      Amazon'dan al
                    </a>
                  </li>
                ))}
              </ul>
            </div>
            {selectedRecipe.steps && Array.isArray(selectedRecipe.steps) && (
              <div style={{ marginTop: 16 }}>
                <strong>Hazƒ±rlanƒ±≈üƒ±:</strong>
                <ol style={{ marginTop: 8 }}>
                  {selectedRecipe.steps.map((step, idx) => (
                    <li key={idx} style={{ marginBottom: 6 }}>
                      {step.children && Array.isArray(step.children)
                        ? step.children.map((c, i) => c.text).join(" ")
                        : ""}
                    </li>
                  ))}
                </ol>
              </div>
            )}
          </div>
        </div>
      )}
      {/* Giri≈ü Yap Modalƒ± */}
      {showLogin && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 2000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={() => setShowLogin(false)}>
          <div style={{ background: "#fff", borderRadius: 16, padding: 32, maxWidth: 400, width: "100%", position: "relative" }} onClick={e => e.stopPropagation()}>
            <button onClick={() => setShowLogin(false)} style={{ position: "absolute", top: 16, right: 16, background: "#e60023", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
            <h2 style={{ marginBottom: 24 }}>Giri≈ü Yap</h2>
            <form onSubmit={e => { e.preventDefault(); const data = { email: e.target.email.value, password: e.target.password.value }; console.log('Giri≈ü:', data); }}>
              <div style={{ marginBottom: 16 }}>
                <label htmlFor="login-email">E-posta</label>
                <input id="login-email" name="email" type="email" required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
              </div>
              <div style={{ marginBottom: 16 }}>
                <label htmlFor="login-password">≈ûifre</label>
                <input id="login-password" name="password" type="password" required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
              </div>
              <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 8, background: '#e60023', color: '#fff', border: 'none', fontWeight: 'bold', fontSize: 16 }}>Giri≈ü Yap</button>
            </form>
          </div>
        </div>
      )}
      {/* √úye Ol Modalƒ± */}
      {showRegister && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 2000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={() => setShowRegister(false)}>
          <div style={{ background: "#fff", borderRadius: 16, padding: 32, maxWidth: 400, width: "100%", position: "relative" }} onClick={e => e.stopPropagation()}>
            <button onClick={() => setShowRegister(false)} style={{ position: "absolute", top: 16, right: 16, background: "#e60023", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
            <h2 style={{ marginBottom: 24 }}>√úye Ol</h2>
            <form onSubmit={e => { e.preventDefault(); const data = { username: e.target.username.value, email: e.target.email.value, password: e.target.password.value }; console.log('√úye Ol:', data); }}>
              <div style={{ marginBottom: 16 }}>
                <label htmlFor="register-username">Kullanƒ±cƒ± Adƒ±</label>
                <input id="register-username" name="username" type="text" required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
              </div>
              <div style={{ marginBottom: 16 }}>
                <label htmlFor="register-email">E-posta</label>
                <input id="register-email" name="email" type="email" required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
              </div>
              <div style={{ marginBottom: 16 }}>
                <label htmlFor="register-password">≈ûifre</label>
                <input id="register-password" name="password" type="password" required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
              </div>
              <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 8, background: '#e60023', color: '#fff', border: 'none', fontWeight: 'bold', fontSize: 16 }}>√úye Ol</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
