import React, { useState } from "react";
import axios from "axios";
import "./ProfileEditModal.css"; // Stil dosyasını unutma

const ProfileEditModal = ({ user, onClose }) => {
  const [username, setUsername] = useState(user.username);
  const [email, setEmail] = useState(user.email);
  const [profileImageBase64, setProfileImageBase64] = useState("");
  const [message, setMessage] = useState("");

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
  const token = localStorage.getItem("token");

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      setProfileImageBase64(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        `${API_BASE_URL}/api/User/UpdateUserProfile`,
        {
          userId: user.userId,
          username,
          email,
          profileImageBase64,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("Profil başarıyla güncellendi!");
      setTimeout(() => {
        setMessage("");
        onClose(); // Modal'ı kapat
      }, 1500);
    } catch (err) {
      console.error("Profil güncelleme hatası:", err);
      setMessage("Profil güncellenirken bir hata oluştu.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Profil Bilgilerini Güncelle</h3>
        <form onSubmit={handleSubmit}>
          <label>
            Kullanıcı Adı:
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </label>
          <label>
            Profil Fotoğrafı:
            <input type="file" accept="image/*" onChange={handleFileChange} />
          </label>
          <button type="submit">Kaydet</button>
          <button type="button" onClick={onClose}>İptal</button>
        </form>
        {message && <p className="status-message">{message}</p>}
      </div>
    </div>
  );
};

export default ProfileEditModal;
