import React, { useEffect, useState } from "react";
import axios from "axios";
import "./RecipeDetailModal.css";

const RecipeDetailModal = ({ recipe, onClose, user }) => {
  const [reviews, setReviews] = useState([]);
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(0);
  const [error, setError] = useState("");
  const [hasReviewed, setHasReviewed] = useState(false);

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
  const token = localStorage.getItem("token");

  // Yorumları getir
  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/Review/GetReviewsByRecipeId/${recipe.id}`);
        setReviews(res.data || []);
        if (user?.userId) {
          const userReview = res.data?.find(r => r.userId === user.userId);
          if (userReview) {
            setHasReviewed(true);
            setComment(userReview.comment);
            setRating(userReview.rating);
          }
        }
      } catch (err) {
        console.error("Yorumlar yüklenemedi:", err);
      }
    };
    if (recipe?.id) fetchReviews();
  }, [recipe?.id, user?.userId]);


  // Yorum gönder
  const handleSubmit = async () => {
    if (!user?.userId) {
      setError("Yorum yapabilmek için giriş yapmalısınız.");
      return;
    }

    if (!comment || rating === 0) {
      setError("Yorum ve puan boş bırakılamaz.");
      return;
    }

    try {
      const res = await axios.post(`${API_BASE_URL}/api/Review/AddReview`, {
        userId: user.userId,
        recipeId: recipe.recipeId,
        comment,
        rating,
      });

      setReviews([...reviews, res.data]);
      setComment("");
      setRating(0);
      setError("");
    } catch (err) {
      console.error("Yorum gönderilemedi:", err);
      setError("Yorum gönderilirken bir hata oluştu.");
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="recipe-modal" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>✖</button>

        <h2>{recipe.recipeName}</h2>
        <img src={recipe.recipeImageUrl} alt={recipe.recipeName} className="recipe-image" />
        <p><strong>Kategori:</strong> {recipe.categoryName}</p>
        <p><strong>Hazırlama Süresi:</strong> {recipe.recipePrepTime} dakika</p>
        
        <p><strong>Malzemeler:</strong></p>
        <ul>
          {recipe.ingredients?.split(",").map((item, i) => <li key={i}>{item.trim()}</li>)}
        </ul>
        <p><strong>Açıklama:</strong> {recipe.recipeDescription}</p>

        <hr />

        <h3>Yorumlar</h3>
        {reviews.length === 0 && <p>Henüz yorum yapılmamış.</p>}
        {reviews.map((rev, i) => (
          <div key={i} className="review">
            <p><strong>{rev.username || "Anonim"}:</strong> {rev.comment}</p>
            <p>⭐ {rev.rating}</p>
          </div>
        ))}

        {user && (
          <div className="review-form">
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Yorumunuzu yazın..."
            />
            <input
              type="number"
              value={rating}
              onChange={(e) => setRating(Number(e.target.value))}
              min={1}
              max={5}
              placeholder="Puan (1-5)"
            />
            <button onClick={handleSubmit}>Gönder</button>
            {error && <p className="error">{error}</p>}
          </div>
        )}
      </div>
    </div>
  );
};

export default RecipeDetailModal;
