import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/tarifs?populate=*")
      .then((response) => {
        setRecipes(response.data.data);
      })
      .catch((error) => {
        console.error("API'den veri alınamadı:", error);
      });
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Yemek Tarifleri</h1>
      {recipes.map((item) => {
        const { id, attributes } = item;
        return (
          <div key={id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
            <h2>{attributes.title}</h2>
            {attributes.image?.data && (
              <img
                src={`http://localhost:1337${attributes.image.data.attributes.url}`}
                alt={attributes.title}
                style={{ width: "200px", borderRadius: "8px" }}
              />
            )}
            <p><strong>Kalori:</strong> {attributes.calories}</p>
            <p><strong>Malzemeler:</strong> {attributes.ingredients}</p>
            <p><strong>Hazırlık Süresi:</strong> {attributes.prep_time} dk</p>
            <p><strong>Vejetaryen mi?:</strong> {attributes.is_vegetarian ? "Evet" : "Hayır"}</p>
          </div>
        );
      })}
    </div>
  );
}

export default App;
