import React, { useEffect, useState, useCallback, useMemo } from "react";
import Navbar from "./components/Navbar";
import RecipeCard from "./components/Recipe/RecipeCard";
import "./App.css";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import ProfilePage from "./components/Profile/ProfilePage";
import RecipeDetailModal from "./components/Recipe/RecipeDetailModal";
import { recipeService } from "./services/recipeService";
import { userService } from "./services/userService";
import { favoriteService } from "./services/favoriteService";
import { getImageUrl } from "./services/api";
import axios from "axios";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Tümü");
  const [categories, setCategories] = useState(["Tümü"]);
  const [userIngredients, setUserIngredients] = useState([]);
  const [inputIngredient, setInputIngredient] = useState("");
  const [showOnlyPossible, setShowOnlyPossible] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [favError, setFavError] = useState("");
  const [theme, setTheme] = useState('light');

  // Filtrelenmiş tarifleri memoize et
  const filteredRecipes = useMemo(() => {
    return recipes.filter((r) => {
      if (!r) return false;
      const title = r.recipeName?.toLowerCase() || "";
      const category = r.category || "";
      const search = searchTerm.toLowerCase();
      const matchesSearch =
        title.includes(search) ||
        category.toLowerCase().includes(search);
      const matchesCategory =
        selectedCategory === "Tümü" ||
        (category && category.trim().toLowerCase() === selectedCategory.trim().toLowerCase());

      // Malzeme bazlı filtreleme
      let matchesIngredients = true;
      if (userIngredients.length > 0) {
        let recipeIngredients = [];
        if (typeof r.recipeIngredients === "string") {
          recipeIngredients = r.recipeIngredients.split(/,|\n/).map(i => i.trim().toLowerCase()).filter(Boolean);
        } else if (Array.isArray(r.recipeIngredients)) {
          recipeIngredients = r.recipeIngredients.map(i => i.trim().toLowerCase());
        }
        matchesIngredients = userIngredients.some(userIng =>
          recipeIngredients.some(recipeIng => recipeIng.includes(userIng))
        );
      }
      return matchesSearch && matchesCategory && matchesIngredients;
    });
  }, [recipes, searchTerm, selectedCategory, userIngredients]);

  // Tema yönetimi
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  // Tarifleri yükle
  useEffect(() => {
    loadRecipes();
  }, []);

  const loadRecipes = async () => {
    try {
      setLoading(true);
      console.log("Tarifler yükleniyor...");
      
      // API çağrısını try-catch ile sarmalayalım
      try {
        // Token ile API çağrısı yap
        const token = localStorage.getItem("token");
        const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
        
        const response = await axios.get(`${API_BASE_URL}/api/Recipe/GetRecipes`, {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`
          }
        });
        
        const recipesData = response.data;
        
        // Resim URL'lerini düzgün şekilde oluştur
        const processedRecipes = recipesData.map(recipe => ({
          ...recipe,
          recipeImageUrl: getImageUrl(recipe.recipeImageUrl)
        }));
        
        setRecipes(processedRecipes);
        
        // Kategorileri çıkar
        const uniqueCategories = [...new Set(
          recipesData
            .map(recipe => recipe?.category)
            .filter(category => category && category.trim() !== "")
        )];
        
        setCategories(["Tümü", ...uniqueCategories]);
        console.log("API'den tarifler başarıyla yüklendi:", recipesData.length);
      } catch (apiError) {
        console.error("API'den tarifler yüklenemedi:", apiError);
        console.error("API Error details:", apiError.response?.data);
        
        // API hatası durumunda demo tarifler göster
