        const demoRecipes = [
          {
            recipeId: 1,
            recipeName: "Mercimek Çorbası",
            recipeImageUrl: "https://images.unsplash.com/photo-1547592166-23ac45744acd?w=400&h=200&fit=crop",
            recipeIngredients: "Kırmızı mercimek, soğan, havuç, tereyağı, tuz, karabiber",
