import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProfileEditModal.css";

const ProfileEditModal = ({ userId, onClose, onProfileUpdated }) => {
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    email: "",
    bio: "",
  });
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");
  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";

  // Kullanıcı bilgilerini getir
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/UserProfile/GetUserProfile/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Profil bilgileri alınamadı:", err);
        setLoading(false);
      }
    };

    if (userId && token) {
      fetchProfile();
    }
  }, [userId, token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`${API_BASE_URL}/api/UserProfile/UpdateUserProfile`, profile, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Profil güncellendi!");
      onProfileUpdated(); // Profil güncellenince parent component bilgilendirilir
      onClose(); // Modal kapatılır
    } catch (err) {
      console.error("Profil güncellenemedi:", err);
      alert("Bir hata oluştu.");
    }
  };

  if (loading) return <div>Yükleniyor...</div>;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Profil Bilgilerini Düzenle</h2>
        <form onSubmit={handleSubmit}>
          <label>Ad:</label>
          <input type="text" name="firstName" value={profile.firstName} onChange={handleChange} required />

          <label>Soyad:</label>
          <input type="text" name="lastName" value={profile.lastName} onChange={handleChange} required />

          <label>Email:</label>
          <input type="email" name="email" value={profile.email} onChange={handleChange} required />

          <label>Hakkımda:</label>
          <textarea name="bio" value={profile.bio} onChange={handleChange} />

          <div className="modal-buttons">
            <button type="submit">Kaydet</button>
            <button type="button" onClick={onClose}>İptal</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfileEditModal;
