import React, { useEffect, useState } from "react";
import axios from "axios";
import RecipeReviewForm from "./RecipeReviewForm";
import { reviewService } from "../../services/reviewService";
import PaginatedRecipeReviewList from "../Review/PaginatedRecipeReviewList";


export default function RecipeDetailModal({ recipe, user, onClose }) {
  const [averageRating, setAverageRating] = useState(null);
  const [reviewCount, setReviewCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAverageRating = async () => {
      try {
        setLoading(true);
        setError("");
        const response = await axios.get(`${API_BASE_URL}/api/reviews/average/${recipe.recipeId}`);
        setAverageRating(response.data.averageRating?.toFixed(1) || null);
        setReviewCount(response.data.reviewCount || 0);
      } catch (err) {
        console.error("Ortalama değerlendirme yüklenemedi:", err);
        setError("Ortalama değerlendirme yüklenemedi.");
      } finally {
        setLoading(false);
      }
    };
    if (recipe.recipeId) {
      fetchAverageRating();
    }
  }, [recipe.recipeId]);
  
  const fetchReviews = async () => {
    try {
      setLoading(true);
      setError("");
      const response = await reviewService.getReviews();
      // Tarif ID'sine göre filtrele
      const recipeReviews = response.data.filter(review => review.recipeId === recipe.recipeId);
      setReviews(recipeReviews);
    } catch (err) {
      console.error("Yorumlar yüklenemedi:", err);
      setError("Yorumlar yüklenemedi.");
    } finally {
      setLoading(false);
    }
  };
  
  useEffect(() => {
    fetchReviews();
  }, [recipe.recipeId]);

  const avgRating =
    reviews.length > 0
      ? (
          reviews.reduce((sum, r) => {
            const rating = r?.rating;
            return sum + (typeof rating === "number" ? rating : 0);
          }, 0) / reviews.length
        ).toFixed(1)
      : null;

  return (
    <div
      className="modal"
      onClick={onClose}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        background: "rgba(0,0,0,0.4)",
        zIndex: 1000,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          background: "var(--modal-bg)",
          color: "var(--text-primary)",
          borderRadius: 16,
          padding: 32,
          maxWidth: 600,
          width: "100%",
          position: "relative",
          maxHeight: "90vh",
          overflowY: "auto",
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          style={{
            position: "absolute",
            top: 16,
            right: 16,
            background: "var(--accent-color)",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            padding: "4px 12px",
            fontSize: 18,
            cursor: "pointer",
          }}
        >
          Kapat
        </button>

        {recipe.recipeImageUrl && (
          <img
            src={recipe.recipeImageUrl}
            alt={recipe.recipeName}
            style={{
              width: "100%",
              height: 300,
              objectFit: "cover",
              borderRadius: 12,
              marginBottom: 20,
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            }}
            onError={(e) => {
              e.target.src = "https://via.placeholder.com/600x300/FF6B6B/FFFFFF?text=Tarif+Resmi";
            }}
          />
        )}

        <h2 style={{ marginBottom: 16, color: "var(--text-primary)" }}>
          {recipe.recipeName}
        </h2>

        <div style={{ marginBottom: 24 }}>
          <h3 style={{ marginBottom: 8, color: "var(--text-primary)" }}>
            Malzemeler:
          </h3>
          <p style={{ lineHeight: 1.6, color: "var(--text-secondary)" }}>
            {recipe.recipeIngredients}
          </p>
        </div>

        <div style={{ marginBottom: 24 }}>
          <h3 style={{ marginBottom: 8, color: "var(--text-primary)" }}>
            Hazırlanışı:
          </h3>
          <p style={{ lineHeight: 1.6, color: "var(--text-secondary)" }}>
            {recipe.recipeInstructions}
          </p>
        </div>

        <div style={{ marginBottom: 24 }}>
          <h3 style={{ marginBottom: 8, color: "var(--text-primary)" }}>
            Değerlendirmeler:
          </h3>
          {avgRating && (
            <div style={{ marginBottom: 8 }}>
              <span style={{ fontSize: 18, fontWeight: "bold" }}>
                ⭐ {avgRating} / 5
              </span>
              <span style={{ color: "var(--text-secondary)", marginLeft: 8 }}>
                ({reviews.length} değerlendirme)
              </span>
            </div>
          )}

          <PaginatedRecipeReviewList recipeId={recipe.recipeId} />
        </div>

        {user && (
          <RecipeReviewForm
            recipeId={recipe.recipeId}
            onReviewSubmit={fetchReviews}
            user={user}
          />
        )}
      </div>
    </div>
  );
}
