import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./components/Navbar";
import RecipeCard from "./components/RecipeCard";
import "./App.css";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import ProfilePage from "./ProfilePage";
import StarRating from "./components/StarRating";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Tümü");
  const [categories, setCategories] = useState(["Tümü"]);
  const [userIngredients, setUserIngredients] = useState([]);
  const [inputIngredient, setInputIngredient] = useState("");
  const [showOnlyPossible, setShowOnlyPossible] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [favError, setFavError] = useState("");
  const [theme, setTheme] = useState('light');

  // Tema yönetimi
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.setAttribute('data-theme', newTheme);
  };

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/tarifs?populate=*")
      .then((res) => {
        setRecipes(res.data.data);
        console.log("Gelen tarifler:", res.data.data);

        // Veritabanından gelen kategorileri al
        const uniqueCategories = [...new Set(
          res.data.data
            .map(recipe => recipe.attributes.category)
            .filter(category => category && category.trim() !== "")
        )];

        setCategories(["Tümü", ...uniqueCategories]);
        console.log("Bulunan kategoriler:", uniqueCategories);
      })
      .catch((err) => console.error("Veri alınamadı:", err));
  }, []);

  // Otomatik giriş için localStorage'dan JWT ile kullanıcıyı çek
  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      axios.get("http://localhost:1337/api/users/me?populate=favorites", {
        headers: { Authorization: `Bearer ${jwt}` }
      })
        .then(res => setUser(res.data))
        .catch(() => setUser(null));
    }
  }, []);



  // Kullanıcı giriş yaptığında favori tarifleri backend'den çek
  useEffect(() => {
    if (user && user.favorites) {
      setFavorites(user.favorites.map(tarif => tarif.id));
    }
  }, [user]);

  // Giriş ve kayıt sonrası kullanıcıyı güncelle
  const handleAuthSuccess = async () => {
    const jwt = localStorage.getItem("jwt");
    if (jwt) {
      try {
        const res = await axios.get("http://localhost:1337/api/users/me?populate=favorites", {
          headers: { Authorization: `Bearer ${jwt}` }
        });
        setUser(res.data);
      } catch {
        setUser(null);
      }
    }
    setShowLogin(false);
    setShowRegister(false);
  };

  // Çıkış fonksiyonu
  const handleLogout = () => {
    localStorage.removeItem("jwt");
    setUser(null);
  };

  // Favori ekle/çıkar (backend)
  const handleFavorite = async (recipeId) => {
    if (!user) return;
    setFavError("");
    let newFavorites;
    if (favorites.includes(recipeId)) {
      newFavorites = favorites.filter((id) => id !== recipeId);
    } else {
      newFavorites = [...favorites, recipeId];
    }
    setFavorites(newFavorites);
    try {
      const jwt = localStorage.getItem("jwt");
      await axios.put(
        `http://localhost:1337/api/users/${user.id}`,
        { favorites: newFavorites },
        { headers: { Authorization: `Bearer ${jwt}` } }
      );
    } catch (err) {
      setFavError("Favori güncellenemedi. Lütfen tekrar deneyin.");
      // Hata olursa eski haline döndür
      setFavorites(favorites);
    }
  };

  // Navbar'daki Profilim butonuna tıklayınca yönlendirme için
  function AppNavbar(props) {
    const navigate = useNavigate();
    return (
      <Navbar
        {...props}
        onProfileClick={() => navigate("/profil")}
        onLogoClick={() => navigate("/")}
        theme={theme}
        toggleTheme={toggleTheme}
      />
    );
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div>
              <AppNavbar
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                selectedCategory={selectedCategory}
                setSelectedCategory={setSelectedCategory}
                categories={categories}
                onLoginClick={() => setShowLogin(true)}
                onRegisterClick={() => setShowRegister(true)}
                user={user}
                onLogout={handleLogout}
                onProfileClick={() => {}}
                onLogoClick={() => {}}
              />
              <div style={{ maxWidth: 500, margin: "24px auto 0", background: "var(--card-bg)", color: "var(--text-primary)", borderRadius: 12, padding: 16, boxShadow: "0 2px 8px var(--shadow-color)", border: "1px solid var(--border-color)" }}>
                <h3 style={{ color: "var(--text-primary)", marginBottom: 16 }}>Elinizdeki Malzemeler</h3>
                <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
                  <input
                    type="text"
                    value={inputIngredient}
                    onChange={e => setInputIngredient(e.target.value)}
                    placeholder="Malzeme ekle..."
                    style={{ flex: 1, padding: 8, borderRadius: 6, border: "1px solid var(--border-color)", backgroundColor: "var(--input-bg)", color: "var(--text-primary)" }}
                    onKeyDown={e => { if (e.key === "Enter" && inputIngredient.trim()) { setUserIngredients([...userIngredients, inputIngredient.trim().toLowerCase()]); setInputIngredient(""); } }}
                  />
                  <button
                    onClick={() => { if (inputIngredient.trim()) { setUserIngredients([...userIngredients, inputIngredient.trim().toLowerCase()]); setInputIngredient(""); } }}
                    style={{ padding: "8px 16px", borderRadius: 6, background: "var(--accent-color)", color: "#fff", border: "none" }}
                  >Ekle</button>
                </div>
                <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginBottom: 12 }}>
                  {userIngredients.map((ing, idx) => (
                    <span key={idx} style={{ background: "var(--bg-secondary)", color: "var(--accent-color)", padding: "4px 10px", borderRadius: 8, fontSize: 15, border: "1px solid var(--border-color)" }}>{ing} <button style={{ background: "none", border: "none", color: "var(--accent-color)", cursor: "pointer" }} onClick={() => setUserIngredients(userIngredients.filter((_, i) => i !== idx))}>×</button></span>
                  ))}
                </div>
                <button
                  onClick={() => setShowOnlyPossible(!showOnlyPossible)}
                  style={{ padding: "8px 16px", borderRadius: 6, background: showOnlyPossible ? "var(--text-secondary)" : "var(--accent-color)", color: "#fff", border: "none", marginTop: 8 }}
                >{showOnlyPossible ? "Tüm Tarifleri Göster" : "Sadece Yapabileceğim Tarifler"}</button>
              </div>
              {favError && <div style={{ color: '#e60023', textAlign: 'center', margin: '12px 0' }}>{favError}</div>}
              <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
                <div className="recipe-grid">
                {recipes
  .filter((r) => {
    if (!r) return false;
    const title = r.title?.toLowerCase() || "";
    const category = r.category || "";
    const search = searchTerm.toLowerCase();
    const matchesSearch =
      title.includes(search) ||
      category.toLowerCase().includes(search);
    const matchesCategory =
      selectedCategory === "Tümü" ||
      (category && category.trim().toLowerCase() === selectedCategory.trim().toLowerCase());

    // Malzeme bazlı filtreleme: Kullanıcının girdiği malzemelerden en az biri tarifteki herhangi bir malzemenin içinde geçiyorsa göster
    let matchesIngredients = true;
    if (userIngredients.length > 0) {
      let recipeIngredients = [];
      if (typeof r.ingredients === "string") {
        recipeIngredients = r.ingredients.split(/,|\n/).map(i => i.trim().toLowerCase()).filter(Boolean);
      } else if (Array.isArray(r.ingredients)) {
        recipeIngredients = r.ingredients.map(i => i.trim().toLowerCase());
      }
      matchesIngredients = userIngredients.some(userIng =>
        recipeIngredients.some(recipeIng => recipeIng.includes(userIng))
      );
    }
    return matchesSearch && matchesCategory && matchesIngredients;
  })
  .map((r) => {
    const { id, title, image, calories, ingredients, prep_time, is_vegetarian } = r;
    // Görsel url'si için:
    let imageUrl = "";
    if (Array.isArray(image) && image.length > 0) {
      imageUrl = "http://localhost:1337" + (image[0].formats?.medium?.url || image[0].url);
    }
    // Eksik malzemeleri bul
    let missingIngredients = [];
    let recipeIngredients = [];
    if (typeof ingredients === "string") {
      recipeIngredients = ingredients.split(/,|\n/).map(i => i.trim().toLowerCase()).filter(Boolean);
    } else if (Array.isArray(ingredients)) {
      recipeIngredients = ingredients.map(i => i.trim().toLowerCase());
    }
    missingIngredients = recipeIngredients.filter(ing => !userIngredients.includes(ing));
    return (
      <RecipeCard
        key={id}
        title={title}
        imageUrl={imageUrl}
        calories={calories}
        ingredients={ingredients}
        prepTime={prep_time}
        isVegetarian={is_vegetarian}
        missingIngredients={missingIngredients}
        userIngredients={userIngredients}
        onClick={() => setSelectedRecipe(r)}
        showIngredients={false}
        isFavorite={favorites.includes(id)}
        onFavoriteClick={() => handleFavorite(id)}
        user={user}
      />
    );
  })
}
                </div>
              </div>
              {selectedRecipe && (
                <RecipeDetailModal
                  recipe={selectedRecipe}
                  user={user}
                  onClose={() => setSelectedRecipe(null)}
                />
              )}
            </div>
          }
        />
        <Route
          path="/profil"
          element={
            <div>
              <AppNavbar
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
                selectedCategory={selectedCategory}
                setSelectedCategory={setSelectedCategory}
                categories={categories}
                onLoginClick={() => setShowLogin(true)}
                onRegisterClick={() => setShowRegister(true)}
                user={user}
                onLogout={handleLogout}
                onProfileClick={() => {}}
                onLogoClick={() => {}}
              />
              <ProfilePage user={user} favorites={favorites} recipes={recipes} />
            </div>
          }
        />
      </Routes>
      {/* Giriş ve Üye Ol modalları */}
      {showLogin && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 2000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={() => setShowLogin(false)}>
          <div style={{ background: "var(--modal-bg)", color: "var(--text-primary)", borderRadius: 16, padding: 32, maxWidth: 400, width: "100%", position: "relative" }} onClick={e => e.stopPropagation()}>
            <button onClick={() => setShowLogin(false)} style={{ position: "absolute", top: 16, right: 16, background: "var(--accent-color)", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
            <h2 style={{ marginBottom: 24, color: "var(--text-primary)" }}>Giriş Yap</h2>
            <LoginForm onSuccess={handleAuthSuccess} />
          </div>
        </div>
      )}
      {showRegister && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 2000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={() => setShowRegister(false)}>
          <div style={{ background: "var(--modal-bg)", color: "var(--text-primary)", borderRadius: 16, padding: 32, maxWidth: 400, width: "100%", position: "relative" }} onClick={e => e.stopPropagation()}>
            <button onClick={() => setShowRegister(false)} style={{ position: "absolute", top: 16, right: 16, background: "var(--accent-color)", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
            <h2 style={{ marginBottom: 24, color: "var(--text-primary)" }}>Üye Ol</h2>
            <RegisterForm onSuccess={handleAuthSuccess} />
          </div>
        </div>
      )}
    </Router>
  );
}

// Validasyonlu LoginForm ve RegisterForm bileşenleri:
function LoginForm({ onSuccess }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});
  const [serverError, setServerError] = useState("");
  const [loading, setLoading] = useState(false);

  function validateEmail(val) {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
  }
  function validatePassword(val) {
    return val.length >= 8 && /[A-Z]/.test(val) && /[a-z]/.test(val) && /[0-9]/.test(val) && /[^A-Za-z0-9]/.test(val);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setServerError("");
    const newErrors = {};
    if (!validateEmail(email)) newErrors.email = "Geçerli bir e-posta girin.";
    if (!validatePassword(password)) newErrors.password = "Şifre en az 8 karakter, büyük/küçük harf, rakam ve özel karakter içermeli.";
    setErrors(newErrors);
    if (Object.keys(newErrors).length === 0) {
      setLoading(true);
      try {
        const res = await axios.post("http://localhost:1337/api/auth/local", {
          identifier: email,
          password
        });
        localStorage.setItem("jwt", res.data.jwt);
        if (onSuccess) onSuccess();
      } catch (err) {
        setServerError(err?.response?.data?.error?.message || "Bir hata oluştu.");
      } finally {
        setLoading(false);
      }
    }
  }



  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="login-email" style={{ color: 'var(--text-primary)' }}>E-posta</label>
        <input id="login-email" name="email" type="email" value={email} onChange={e => setEmail(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid var(--border-color)', marginTop: 4, backgroundColor: 'var(--input-bg)', color: 'var(--text-primary)' }} />
        {errors.email && <div style={{ color: 'var(--error-color)', fontSize: 13 }}>{errors.email}</div>}
      </div>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="login-password" style={{ color: 'var(--text-primary)' }}>Şifre</label>
        <input id="login-password" name="password" type="password" value={password} onChange={e => setPassword(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid var(--border-color)', marginTop: 4, backgroundColor: 'var(--input-bg)', color: 'var(--text-primary)' }} />
        {errors.password && <div style={{ color: 'var(--error-color)', fontSize: 13 }}>{errors.password}</div>}
      </div>
      {serverError && <div style={{ color: 'var(--error-color)', fontSize: 14, marginBottom: 12 }}>{serverError}</div>}
      <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 8, background: 'var(--accent-color)', color: '#fff', border: 'none', fontWeight: 'bold', fontSize: 16 }} disabled={Object.keys(errors).length > 0 || !email || !password || loading}>{loading ? "Giriş Yapılıyor..." : "Giriş Yap"}</button>
    </form>
  );
}

function RegisterForm({ onSuccess }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});
  const [serverError, setServerError] = useState("");
  const [loading, setLoading] = useState(false);

  function validateEmail(val) {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
  }
  function validatePassword(val) {
    return val.length >= 8 && /[A-Z]/.test(val) && /[a-z]/.test(val) && /[0-9]/.test(val) && /[^A-Za-z0-9]/.test(val);
  }
  function validateUsername(val) {
    return val.length >= 3 && /^[A-Za-z0-9_]+$/.test(val);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setServerError("");
    const newErrors = {};
    if (!validateUsername(username)) newErrors.username = "Kullanıcı adı en az 3 karakter, harf/rakam/alt çizgi içermeli.";
    if (!validateEmail(email)) newErrors.email = "Geçerli bir e-posta girin.";
    if (!validatePassword(password)) newErrors.password = "Şifre en az 8 karakter, büyük/küçük harf, rakam ve özel karakter içermeli.";
    setErrors(newErrors);
    if (Object.keys(newErrors).length === 0) {
      setLoading(true);
      try {
        const res = await axios.post("http://localhost:1337/api/auth/local/register", {
          username,
          email,
          password
        });
        localStorage.setItem("jwt", res.data.jwt);
        if (onSuccess) onSuccess();
      } catch (err) {
        setServerError(err?.response?.data?.error?.message || "Bir hata oluştu.");
      } finally {
        setLoading(false);
      }
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="register-username" style={{ color: 'var(--text-primary)' }}>Kullanıcı Adı</label>
        <input id="register-username" name="username" type="text" value={username} onChange={e => setUsername(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid var(--border-color)', marginTop: 4, backgroundColor: 'var(--input-bg)', color: 'var(--text-primary)' }} />
        {errors.username && <div style={{ color: 'var(--error-color)', fontSize: 13 }}>{errors.username}</div>}
      </div>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="register-email" style={{ color: 'var(--text-primary)' }}>E-posta</label>
        <input id="register-email" name="email" type="email" value={email} onChange={e => setEmail(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid var(--border-color)', marginTop: 4, backgroundColor: 'var(--input-bg)', color: 'var(--text-primary)' }} />
        {errors.email && <div style={{ color: 'var(--error-color)', fontSize: 13 }}>{errors.email}</div>}
      </div>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="register-password" style={{ color: 'var(--text-primary)' }}>Şifre</label>
        <input id="register-password" name="password" type="password" value={password} onChange={e => setPassword(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid var(--border-color)', marginTop: 4, backgroundColor: 'var(--input-bg)', color: 'var(--text-primary)' }} />
        {errors.password && <div style={{ color: 'var(--error-color)', fontSize: 13 }}>{errors.password}</div>}
      </div>
      {serverError && <div style={{ color: 'var(--error-color)', fontSize: 14, marginBottom: 12 }}>{serverError}</div>}
      <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 8, background: 'var(--accent-color)', color: '#fff', border: 'none', fontWeight: 'bold', fontSize: 16 }} disabled={Object.keys(errors).length > 0 || !username || !email || !password || loading}>{loading ? "Üye Olunuyor..." : "Üye Ol"}</button>
    </form>
  );
}

// Yeni RecipeDetailModal bileşeni:
function RecipeDetailModal({ recipe, user, onClose }) {
  const [reviews, setReviews] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState("");

  // Yorumları çek
  React.useEffect(() => {
    setLoading(true);
    setError("");
    axios
      .get(`http://localhost:1337/api/reviews?populate=users_permissions_users,tarifs&filters[tarifs][id][$eq]=${recipe.id}`)
      .then(res => {
        setReviews(res.data.data || []);
        setLoading(false);
      })
      .catch(() => {
        setError("Yorumlar yüklenemedi.");
        setLoading(false);
      });
  }, [recipe.id]);

  // Yorum gönderilince güncelle
  const handleReviewAdded = () => {
    setLoading(true);
    axios
      .get(`http://localhost:1337/api/reviews?populate=users_permissions_users,tarifs&filters[tarifs][id][$eq]=${recipe.id}`)
      .then(res => {
        setReviews(res.data.data || []);
        setLoading(false);
      })
      .catch(() => {
        setError("Yorumlar yüklenemedi.");
        setLoading(false);
      });
  };

  // Ortalama puan hesapla
  const avgRating = reviews.length > 0 ? (reviews.reduce((sum, r) => sum + (r.attributes.rating || 0), 0) / reviews.length).toFixed(1) : null;

  return (
    <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.4)", zIndex: 1000, display: "flex", alignItems: "center", justifyContent: "center" }} onClick={onClose}>
      <div style={{ background: "var(--modal-bg)", color: "var(--text-primary)", borderRadius: 16, padding: 32, maxWidth: 600, width: "100%", position: "relative", maxHeight: "90vh", overflowY: "auto" }} onClick={e => e.stopPropagation()}>
        <button onClick={onClose} style={{ position: "absolute", top: 16, right: 16, background: "var(--accent-color)", color: "#fff", border: "none", borderRadius: 8, padding: "4px 12px", fontSize: 18, cursor: "pointer" }}>Kapat</button>
        <img src={(() => { let imageUrl = ""; const image = recipe.image; if (Array.isArray(image) && image.length > 0) { imageUrl = "http://localhost:1337" + (image[0].formats?.medium?.url || image[0].url); } return imageUrl; })()} alt={recipe.title} style={{ width: "100%", borderRadius: 12, marginBottom: 16 }} />
        <h2 style={{ color: "var(--text-primary)" }}>{recipe.title}</h2>
        <div style={{ color: "var(--accent-color)", marginBottom: 8 }}>
          <span>🔥 {recipe.calories} kalori</span> | <span>⏱️ {recipe.prep_time} dk</span> {recipe.is_vegetarian ? "| 🌱 Vejetaryen" : ""}
        </div>
        <p style={{ marginBottom: 16, color: "var(--text-secondary)" }}>{recipe.description}</p>
        <div>
          <strong style={{ color: "var(--text-primary)" }}>Malzemeler:</strong>
          <ul style={{ color: "var(--text-primary)" }}>
            {(typeof recipe.ingredients === "string"
              ? recipe.ingredients.split(/,|\n/).map(i => i.trim()).filter(Boolean)
              : Array.isArray(recipe.ingredients)
              ? recipe.ingredients
              : []
            ).map((item, idx) => (
              <li key={idx} style={{ marginBottom: 4 }}>
                {item}
                <a
                  href={`https://www.amazon.com.tr/s?k=${encodeURIComponent(item)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="amazon-btn"
                  style={{ marginLeft: 8 }}
                >
                  Amazon'dan al
                </a>
              </li>
            ))}
          </ul>
        </div>
        {recipe.steps && Array.isArray(recipe.steps) && (
          <div style={{ marginTop: 16 }}>
            <strong style={{ color: "var(--text-primary)" }}>Hazırlanışı:</strong>
            <ol style={{ marginTop: 8, color: "var(--text-primary)" }}>
              {recipe.steps.map((step, idx) => (
                <li key={idx} style={{ marginBottom: 6 }}>
                  {step.children && Array.isArray(step.children)
                    ? step.children.map((c, i) => c.text).join(" ")
                    : ""}
                </li>
              ))}
            </ol>
          </div>
        )}
        {/* Ortalama puan */}
        <div style={{ marginTop: 24, marginBottom: 8 }}>
          <strong style={{ color: "var(--text-primary)", fontSize: 18 }}>Ortalama Puan: </strong>
          {avgRating ? <span style={{ fontSize: 18, color: "#FFD600", fontWeight: 600 }}>{avgRating} / 5</span> : <span style={{ color: "var(--text-secondary)" }}>Henüz puan yok</span>}
        </div>
        {/* Yorum formu */}
        <div style={{ marginTop: 8, borderTop: "1px solid var(--border-color)", paddingTop: 16 }}>
          <h3 style={{ color: "var(--text-primary)", marginBottom: 12 }}>Tarif Puanla & Yorum Yap</h3>
          {user ? (
            <RecipeReviewForm recipeId={recipe.id} user={user} onReviewAdded={handleReviewAdded} />
          ) : (
            <div style={{ color: "var(--text-secondary)", marginBottom: 12 }}>Yorum ve puan vermek için giriş yapmalısınız.</div>
          )}
        </div>
        {/* Yorumlar */}
        <div style={{ marginTop: 24 }}>
          <h4 style={{ color: "var(--text-primary)", marginBottom: 8 }}>Yorumlar</h4>
          {loading ? (
            <div style={{ color: "var(--text-secondary)" }}>Yükleniyor...</div>
          ) : error ? (
            <div style={{ color: "var(--error-color)" }}>{error}</div>
          ) : reviews.length === 0 ? (
            <div style={{ color: "var(--text-secondary)" }}>Henüz yorum yok.</div>
          ) : (
            reviews.map((r, idx) => (
              <div key={r.id || idx} style={{ marginBottom: 12, background: "var(--bg-secondary)", borderRadius: 8, padding: 10 }}>
                <StarRating value={r.attributes.rating} size={20} disabled /> <span style={{ color: "var(--text-secondary)", fontSize: 14, marginLeft: 8 }}>{r.attributes.users_permissions_users?.data?.[0]?.username || "Kullanıcı"}</span>
                <div style={{ color: "var(--text-secondary)", fontSize: 15 }}>{r.attributes.comment}</div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

// RecipeReviewForm bileşenini güncelle:
function RecipeReviewForm({ recipeId, user, onReviewAdded }) {
  const [rating, setRating] = React.useState(0);
  const [comment, setComment] = React.useState("");
  const [loading, setLoading] = React.useState(false);
  const [success, setSuccess] = React.useState(false);
  const [error, setError] = React.useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess(false);
    if (!rating || !comment.trim()) {
      setError("Lütfen puan ve yorum girin.");
      return;
    }
    setLoading(true);
    try {
      const jwt = localStorage.getItem("jwt");
      await axios.post(
        "http://localhost:1337/api/reviews",
        {
          data: {
            rating,
            comment,
            user: user.id, // DİKKAT: Alan adı user!
            tarif: recipeId, // Eğer tarif ile ilişki varsa
          },
        },
        { headers: { Authorization: `Bearer ${jwt}` } }
      );
      setSuccess(true);
      setRating(0);
      setComment("");
      if (onReviewAdded) onReviewAdded();
    } catch (err) {
      setError("Yorum kaydedilemedi. Lütfen tekrar deneyin.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 16 }}>
      <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 8 }}>
        <StarRating value={rating} onChange={setRating} />
        <span style={{ color: "var(--text-secondary)", fontSize: 15 }}>{rating > 0 ? `${rating} / 5` : "Puan ver"}</span>
      </div>
      <textarea
        value={comment}
        onChange={e => setComment(e.target.value)}
        placeholder="Yorumunuzu yazın..."
        rows={3}
        style={{ width: "100%", borderRadius: 8, border: "1px solid var(--border-color)", padding: 8, fontSize: 15, background: "var(--input-bg)", color: "var(--text-primary)", marginBottom: 8 }}
      />
      {error && <div style={{ color: "var(--error-color)", marginBottom: 8 }}>{error}</div>}
      {success && <div style={{ color: "var(--success-color)", marginBottom: 8 }}>Yorumunuz kaydedildi!</div>}
      <button type="submit" disabled={loading} style={{ background: "var(--accent-color)", color: "#fff", border: "none", borderRadius: 8, padding: "8px 20px", fontWeight: 500, fontSize: 16, cursor: loading ? "not-allowed" : "pointer" }}>
        {loading ? "Gönderiliyor..." : "Gönder"}
      </button>
    </form>
  );
}

export default App;
