import React from "react";
import RecipeCard from "./RecipeCard";

const RecipeList = ({ recipes, favorites, onAddFavorite, onRemoveFavorite }) => {
  return (
    <div className="recipe-list">
      {recipes.length === 0 ? (
        <p>Tarif bulunamadÄ±.</p>
      ) : (
        recipes.map((recipe) => {
          const isFavorite = favorites.some((fav) => fav.recipeId === recipe.id);
          return (
            <RecipeCard
              key={recipe.id}
              recipe={recipe}
              isFavorite={isFavorite}
              onAddFavorite={() => onAddFavorite(recipe.id)}
              onRemoveFavorite={() => onRemoveFavorite(recipe.id)}
            />
          );
        })
      )}
    </div>
  );
};

export default RecipeList;
