import React, { memo } from 'react';

const SearchInput = memo(({ value, onChange, placeholder = "Tarif ara..." }) => {
  return (
    <input
      type="text"
      placeholder={placeholder}
      className="search-input"
      value={value}
      onChange={onChange}
      onKeyDown={(e) => {
        // Enter tuşuna basıldığında form submit'i engelle
        if (e.key === 'Enter') {
          e.preventDefault();
        }
      }}
    />
  );
});

export default SearchInput;
