import React, { useEffect, useState } from "react";
import axios from "axios";
import RecipeCard from "./components/RecipeCard";

function App() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/tarifs?populate=*")
      .then((response) => {
        setRecipes(response.data.data);
      })
      .catch((error) => {
        console.error("API'den veri alƒ±namadƒ±:", error);
      });
  }, []);

  return (
    <div style={{ padding: "32px", maxWidth: "800px", margin: "0 auto" }}>
      <h1>üçΩÔ∏è Yemek Tarifleri</h1>
      <input
    type="text"
    placeholder="Tarif ara..."
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    style={{
      padding: "10px",
      margin: "20px 0",
      width: "100%",
      borderRadius: "8px",
      border: "1px solid #ccc",
      fontSize: "16px",
    }}
/>
      {recipes.map(({ id, attributes }) => (
        <RecipeCard
          key={id}
          title={attributes.title}
          imageUrl={`http://localhost:1337${attributes.image?.data?.attributes?.url}`}
          calories={attributes.calories}
          ingredients={attributes.ingredients}
          prepTime={attributes.prep_time}
          isVegetarian={attributes.is_vegetarian}
        />
      ))}
    </div>
  );

  const [searchTerm, setSearchTerm] = useState("");
}

export default App;
