import React, { useState } from "react";
import RecipeCard from "./RecipeCard";
import RecipeDetailModal from "./RecipeDetailModal";

const RecipeList = ({ recipes, favorites, onAddFavorite, onRemoveFavorite, user }) => {
  const [selectedRecipe, setSelectedRecipe] = useState(null); // Modal için seçilen tarif

  const handleCardClick = (recipe) => {
    setSelectedRecipe(recipe); // Tarife tıklanınca modal açılır
  };

  const handleCloseModal = () => {
    setSelectedRecipe(null); // Modal kapatılır
  };

  return (
    <div className="recipe-list">
      {recipes.length === 0 && <p>Tarif bulunamadı.</p>}

      {recipes.map((recipe) => {
        const isFavorite = favorites.some((fav) => fav.recipeId === recipe.recipeId);

        return (
          <RecipeCard
            key={recipe.recipeId}
            recipe={recipe}
            isFavorite={isFavorite}
            onClick={() => handleCardClick(recipe)}
            onAddFavorite={() => onAddFavorite(recipe.recipeId)}
            onRemoveFavorite={() => onRemoveFavorite(recipe.recipeId)}
          />
        );
      })}

      {selectedRecipe && (
        <RecipeDetailModal
          recipe={selectedRecipe}
          onClose={handleCloseModal}
          user={user}
        />
      )}
    </div>
  );
};

export default RecipeList;
