import React, { useEffect, useState } from "react";
import { reviewService } from "../../services/reviewService";

const PaginatedRecipeReviewList = ({ recipeId }) => {
  const [reviews, setReviews] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const pageSize = 10;

  useEffect(() => {
    if (!recipeId) return;

    const fetchReviews = async () => {
      try {
        const result = await reviewService.getPagedReviewsByRecipe(recipeId, page, pageSize);
        setReviews(result.data);
        setTotalPages(result.totalPages);
      } catch (err) {
        console.error("Yorumlar alınırken hata:", err);
      }
    };

    fetchReviews();
  }, [recipeId, page]);

  const handlePageClick = (pageNum) => {
    if (pageNum !== page) setPage(pageNum);
  };

  return (
    <div style={{ marginTop: "20px", display: "flex", justifyContent: "center" }}>
  <ul style={{
    display: "flex",
    listStyleType: "none",
    padding: 0,
    border: "1px solid #ccc",
    borderRadius: "4px",
    overflow: "hidden"
  }}>
    {/* Önceki Sayfa */}
    <li
      onClick={() => page > 1 && setPage(page - 1)}
      style={{
        padding: "8px 12px",
        cursor: page > 1 ? "pointer" : "not-allowed",
        backgroundColor: "#fff",
        borderRight: "1px solid #ccc"
      }}
    >
      «
    </li>

    {/* Sayfa Numaraları */}
    {[...Array(totalPages)].map((_, index) => {
      const pageNum = index + 1;
      return (
        <li
          key={pageNum}
          onClick={() => setPage(pageNum)}
          style={{
            padding: "8px 12px",
            cursor: "pointer",
            backgroundColor: page === pageNum ? "#4CAF50" : "#fff",
            color: page === pageNum ? "#fff" : "#000",
            fontWeight: page === pageNum ? "bold" : "normal",
            borderRight: pageNum !== totalPages ? "1px solid #ccc" : "none"
          }}
        >
          {pageNum}
        </li>
      );
    })}

    {/* Sonraki Sayfa */}
    <li
      onClick={() => page < totalPages && setPage(page + 1)}
      style={{
        padding: "8px 12px",
        cursor: page < totalPages ? "pointer" : "not-allowed",
        backgroundColor: "#fff"
      }}
    >
      »
    </li>
  </ul>
</div>

  );
};

export default PaginatedRecipeReviewList;
