import React from "react";
import "./RecipeCard.css";

const RecipeCard = ({ title, imageUrl, calories, ingredients, prepTime, isVegetarian }) => {
  return (
    <div className="recipe-card">
      <img src={imageUrl} alt={title} />
      <div className="recipe-content">
        <h2>{title}</h2>
        <p><strong>Kalori:</strong> {calories}</p>
        <p><strong>Hazırlık Süresi:</strong> {prepTime} dk</p>
        <p><strong>Vejetaryen mi?:</strong> {isVegetarian ? "Evet" : "Hayır"}</p>
        <p><strong>Malzemeler:</strong> {ingredients}</p>
      </div>
    </div>
  );
};

export default RecipeCard;
