import React from "react";
import "./RecipeCard.css";

const RecipeCard = ({ title, imageUrl, calories, ingredients, prepTime, isVegetarian, missingIngredients = [], userIngredients = [], onClick, showIngredients = false, isFavorite = false, onFavoriteClick, user }) => {
  // Malzemeleri diziye √ßevir (virg√ºl veya satƒ±r ba≈üƒ±na g√∂re)
  let ingredientList = [];
  if (typeof ingredients === "string") {
    ingredientList = ingredients.split(/,|\n/).map(i => i.trim()).filter(Boolean);
  } else if (Array.isArray(ingredients)) {
    ingredientList = ingredients;
  }

  return (
    <div className="recipe-card modern" onClick={onClick} style={{ cursor: onClick ? "pointer" : undefined, position: "relative" }}>
      <img src={imageUrl} alt={title} />
      {/* Favori butonu */}
      {user && (
        <button
          className="favorite-btn"
          onClick={e => { e.stopPropagation(); if (onFavoriteClick) onFavoriteClick(); }}
          aria-label={isFavorite ? "Favorilerden √ßƒ±kar" : "Favorilere ekle"}
        >
          {isFavorite ? "‚ù§Ô∏è" : "ü§ç"}
        </button>
      )}
      <div className="recipe-content">
        <h2>{title}</h2>
        {showIngredients && (
          <>
            <div className="recipe-meta">
              <span>üî• {calories} kalori</span>
              <span>‚è±Ô∏è {prepTime} dk</span>
              <span>{isVegetarian ? "üå± Vejetaryen" : ""}</span>
            </div>
            <div className="ingredients-list">
              <strong>Malzemeler:</strong>
              <ul>
                {ingredientList.map((item, idx) => {
                  const itemLower = item.toLowerCase();
                  const isMissing = missingIngredients.includes(itemLower);
                  const isUserHas = userIngredients.includes(itemLower);
                  return (
                    <li key={idx} style={{ color: isMissing ? "#e60023" : isUserHas ? "#1db954" : undefined }}>
                      {item}
                      <a
                        href={`https://www.amazon.com.tr/s?k=${encodeURIComponent(item)}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="amazon-btn"
                        onClick={e => e.stopPropagation()}
                      >
                        Amazon'dan al
                      </a>
                      {isUserHas ? (
                        <span style={{ marginLeft: 8, color: "#1db954", fontWeight: "bold" }}>‚úî</span>
                      ) : null}
                    </li>
                  );
                })}
              </ul>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default RecipeCard;
