import React, { useEffect, useState } from "react";
import axios from "axios";
import RecipeReviewForm from "./RecipeReviewForm";
import StarRating from "./StarRating";

export default function RecipeDetailModal({ recipe, user, onClose }) {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  // Yorumları Strapi'den çek
  const fetchReviews = () => {
    setLoading(true);
    axios
      .get(
        `http://localhost:1337/api/reviews?filters[tarif][id][$eq]=${recipe.id}&populate[user]=*&populate[tarif]=true`
      )
      .then((res) => {
        console.log("🔍 Gelen yorumlar:", res.data.data);
        setReviews(res.data.data || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error("⛔️ Yorum yükleme hatası:", err.response?.data || err.message);
        setError("Yorumlar yüklenemedi.");
        setLoading(false);
      });
  };
  
  
  useEffect(() => {
    fetchReviews();
  }, [recipe.id]);

  const avgRating =
    reviews.length > 0
      ? (
          reviews.reduce((sum, r) => {
            const rating = r?.attributes?.rating;
            return sum + (typeof rating === "number" ? rating : 0);
          }, 0) / reviews.length
        ).toFixed(1)
      : null;

  const imageUrl =
    Array.isArray(recipe.image) && recipe.image.length > 0
      ? "http://localhost:1337" +
        (recipe.image[0].formats?.medium?.url || recipe.image[0].url)
      : "";

  return (
    <div
      className="modal"
      onClick={onClose}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        background: "rgba(0,0,0,0.4)",
        zIndex: 1000,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          background: "var(--modal-bg)",
          color: "var(--text-primary)",
          borderRadius: 16,
          padding: 32,
          maxWidth: 600,
          width: "100%",
          position: "relative",
          maxHeight: "90vh",
          overflowY: "auto",
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          style={{
            position: "absolute",
            top: 16,
            right: 16,
            background: "var(--accent-color)",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            padding: "4px 12px",
            fontSize: 18,
            cursor: "pointer",
          }}
        >
          Kapat
        </button>

        {imageUrl && (
          <img
            src={imageUrl}
            alt={recipe.title}
            style={{ width: "100%", borderRadius: 12, marginBottom: 16 }}
          />
        )}
        <h2>{recipe.title}</h2>
        <div style={{ color: "var(--accent-color)", marginBottom: 8 }}>
          <span>🔥 {recipe.calories} kalori</span> |{" "}
          <span>⏱️ {recipe.prep_time} dk</span>{" "}
          {recipe.is_vegetarian ? "| 🌱 Vejetaryen" : ""}
        </div>
        <p>{recipe.description}</p>

        <strong>Malzemeler:</strong>
        <ul>
          {(typeof recipe.ingredients === "string"
            ? recipe.ingredients.split(/,|\n/).map((i) => i.trim()).filter(Boolean)
            : Array.isArray(recipe.ingredients)
            ? recipe.ingredients
            : []
          ).map((item, idx) => (
            <li key={idx}>
              {item}
              <a
                href={`https://www.amazon.com.tr/s?k=${encodeURIComponent(item)}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{ marginLeft: 8 }}
              >
                Amazon'dan al
              </a>
            </li>
          ))}
        </ul>

        {recipe.steps && Array.isArray(recipe.steps) && (
          <div style={{ marginTop: 16 }}>
            <strong>Hazırlanışı:</strong>
            <ol>
              {recipe.steps.map((step, idx) => (
                <li key={idx}>
                  {step.children?.map((c) => c.text).join(" ") || ""}
                </li>
              ))}
            </ol>
          </div>
        )}

        <div style={{ marginTop: 24 }}>
          <strong style={{ fontSize: 18 }}>Ortalama Puan: </strong>
          {avgRating ? (
            <span
              style={{
                fontSize: 18,
                color: "#FFD600",
                fontWeight: 600,
              }}
            >
              {avgRating} / 5
            </span>
          ) : (
            <span>Henüz puan yok</span>
          )}
        </div>

        {/* Yorum formu */}
        <div
          style={{
            marginTop: 16,
            borderTop: "1px solid var(--border-color)",
            paddingTop: 16,
          }}
        >
          <h3>Tarif Puanla & Yorum Yap</h3>
          {user ? (
            <RecipeReviewForm
              recipeId={recipe.id}
              user={user}
              onReviewAdded={fetchReviews}
              rating={rating}
              onRatingChange={setRating}
              comment={comment}
              onCommentChange={setComment}
            />
          ) : (
            <p>Yorum yapmak için giriş yapın.</p>
          )}
        </div>

        {/* Yorum listesi */}
        <div style={{ marginTop: 24 }}>
          <h4>Yorumlar</h4>
          {loading ? (
            <p>Yükleniyor...</p>
          ) : error ? (
            <p>{error}</p>
          ) : reviews.length === 0 ? (
            <p>Henüz yorum yok.</p>
          ) : (

            reviews.map((r) => {
              const comment = r?.attributes?.comment || "";
              const rating = r?.attributes?.rating || 0;
              const username = r?.attributes?.user?.data?.attributes?.username || "Anonim";
            
              return (
                <div key={r.id} style={{
                  marginBottom: 12,
                  background: "var(--bg-secondary)",
                  borderRadius: 8,
                  padding: 10,
                }}>
                  <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 8 }}>
                    <b style={{ color: "var(--accent-color)" }}>{username}</b>
                    <div style={{ display: "flex", alignItems: "center", gap: 4 }}>
                      <span style={{ color: "#FFD600", fontSize: 16 }}>⭐</span>
                      <span style={{ fontWeight: 600 }}>{rating} / 5</span>
                    </div>
                  </div>
                  <p style={{ margin: 0, lineHeight: 1.5 }}>{comment}</p>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}
