import React, { memo, useCallback, useRef, useEffect } from 'react';

const SearchInput = memo(({ value, onChange, placeholder = "Tarif ara..." }) => {
  const inputRef = useRef(null);

  const handleChange = useCallback((e) => {
    onChange(e);
  }, [onChange]);

  const handleKeyDown = useCallback((e) => {
    // Enter tuşuna basıldığında form submit'i engelle
    if (e.key === 'Enter') {
      e.preventDefault();
    }
  }, []);

  // Input'un focus'unu koru
  useEffect(() => {
    if (inputRef.current && document.activeElement === inputRef.current) {
      const cursorPosition = inputRef.current.selectionStart;
      const inputLength = inputRef.current.value.length;
      
      // Kısa bir gecikme ile focus'u geri ver
      const timer = setTimeout(() => {
        if (inputRef.current) {
          inputRef.current.focus();
          // Cursor pozisyonunu koru
          const newPosition = Math.min(cursorPosition, inputLength);
          inputRef.current.setSelectionRange(newPosition, newPosition);
        }
      }, 0);
      
      return () => clearTimeout(timer);
    }
  }, [value]);

  return (
    <input
      ref={inputRef}
      type="text"
      placeholder={placeholder}
      className="search-input"
      value={value}
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      autoComplete="off"
      spellCheck="false"
    />
  );
});

export default SearchInput;
