import React from "react";
import "./Navbar.css";

const Navbar = ({ searchTerm, setSearchTerm, selectedCategory, setSelectedCategory, categories, onLoginClick, onRegisterClick }) => {
  console.log("Navbar kategorileri:", categories);

  return (
    <nav className="navbar">
      <div style={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <h2 className="logo">Tarif Kapıda</h2>
        <div style={{ display: "flex", gap: 8 }}>
          <button className="nav-auth-btn" onClick={onLoginClick}>Giriş Yap</button>
          <button className="nav-auth-btn" onClick={onRegisterClick}>Üye Ol</button>
        </div>
      </div>

      <input
        type="text"
        placeholder="Tarif ara..."
        className="search-input"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="nav-links">
        {categories.map((cat) => (
          <button
            key={cat}
            className={selectedCategory === cat ? "active-category" : ""}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
