import React, { useEffect, useState } from "react";
import axios from "axios";
import ProfileEditModal from "../components/Profile/ProfileEditModal";
import "./ProfilePage.css";

const ProfilePage = ({ user }) => {
  const [favorites, setFavorites] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [userProfile, setUserProfile] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [loading, setLoading] = useState(true);

  const API_BASE_URL = localStorage.getItem("API_BASE_URL");
  const token = localStorage.getItem("token");

  // Kullanƒ±cƒ± profilini getir
  useEffect(() => {
    if (!user?.userId) return;

    const fetchUserProfile = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/UserProfile/GetUserProfile/${user.userId}`);
        console.log("Backend'den gelen profil:", res.data);
        console.log("Fotoƒüraf deƒüeri:", res.data?.profileImageBase64);
        setUserProfile(res.data);
      } catch (err) {
        console.error("Profil y√ºklenemedi:", err);
        // Profil yoksa varsayƒ±lan deƒüerler kullan
        setUserProfile({
          bio: "Havalƒ± bir kediyim.",
          profileImageBase64: null
        });
      }
    };

    fetchUserProfile();
  }, [user]);

  // Favori tarifleri getir
  useEffect(() => {
    console.log("ProfilePage useEffect - user:", user);
    console.log("ProfilePage useEffect - token:", token);

    if (!user?.userId || !token) {
      console.log("ProfilePage: user veya token eksik");
      return;
    }

    const fetchFavorites = async () => {
      try {
        console.log("Favoriler y√ºkleniyor...");
        const res = await axios.get(`${API_BASE_URL}/api/Favorite/GetUserFavorites/${user.userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFavorites(res.data || []);
        console.log("Favoriler y√ºklendi:", res.data);
      } catch (err) {
        console.error("Favoriler y√ºklenemedi:", err);
        setFavorites([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFavorites();
  }, [user, token]);

  // Kullanƒ±cƒ±nƒ±n yaptƒ±ƒüƒ± yorumlarƒ± getir
  useEffect(() => {
    if (!user?.userId) return;

    const fetchReviews = async () => {
      try {
        // Backend'de kullanƒ±cƒ±ya g√∂re yorum getirme endpoint'i yok, 
        // ≈üimdilik bo≈ü array kullan
        setReviews([]);
      } catch (err) {
        console.error("Yorumlar y√ºklenemedi:", err);
        setReviews([]);
      }
    };

    fetchReviews();
  }, [user]);

  if (!user || !user.userId) {
    return (
      <div className="profile-page">
        <div className="empty-state">
          <div className="empty-state-icon">üîí</div>
          <div className="empty-state-text">Giri≈ü yapmanƒ±z gerekiyor</div>
          <div className="empty-state-subtext">Profil sayfasƒ±nƒ± g√∂r√ºnt√ºlemek i√ßin l√ºtfen giri≈ü yapƒ±n.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      {/* Profil Ba≈ülƒ±ƒüƒ± */}
      <div className="profile-header">
        <h1 className="profile-title">Profil Sayfasƒ±</h1>
        <p className="profile-subtitle">Ho≈ü geldin, {user.username}! üëã</p>
      </div>

      {/* Profil ƒ∞√ßeriƒüi */}
      <div className="profile-content">
        {/* Sol Sidebar - Profil Bilgileri */}
        <div className="profile-sidebar">
          {/* Profil Fotoƒürafƒ± */}
          {userProfile?.profileImageBase64 ? (
            <img 
              src={userProfile.profileImageBase64.startsWith('data:') 
                ? userProfile.profileImageBase64 
                : `${API_BASE_URL}${userProfile.profileImageBase64}`
              }
              alt="Profil Fotoƒürafƒ±"
              className="profile-photo"
              onError={(e) => {
                console.log("Fotoƒüraf y√ºklenemedi, placeholder g√∂steriliyor");
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
          ) : null}
          <div className="profile-photo-placeholder" style={{ display: userProfile?.profileImageBase64 ? 'none' : 'flex' }}>
            üë§
          </div>

          {/* Profil Bilgileri */}
          <div className="profile-section">
            <h3 className="section-title">
              <span className="section-title-icon">üë§</span>
              Bilgiler
            </h3>
            <div className="profile-info">
              <div className="info-item">
                <span className="info-label">Kullanƒ±cƒ± Adƒ±:</span>
                <span className="info-value">{user.username}</span>
              </div>
              <div className="info-item">
                <span className="info-label">E-posta:</span>
                <span className="info-value">{user.email}</span>
              </div>
              {userProfile?.bio && (
                <div className="info-item">
                  <span className="info-label">Hakkƒ±mda:</span>
                  <span className="info-value">{userProfile.bio}</span>
                </div>
              )}
            </div>

            {/* Profil D√ºzenleme Butonu */}
            <div className="profile-actions">
              <button 
                className="btn btn-primary"
                onClick={() => setShowEditModal(true)}
              >
                ‚úèÔ∏è Profili D√ºzenle
              </button>
            </div>
          </div>
        </div>

        {/* Saƒü Ana ƒ∞√ßerik */}
        <div className="profile-main">
          {/* Favori Tarifler */}
          <div className="profile-section">
            <h3 className="section-title">
              <span className="section-title-icon">‚ù§Ô∏è</span>
              Favori Tarifler
            </h3>
            {loading ? (
              <div className="empty-state">
                <div className="empty-state-icon">‚è≥</div>
                <div className="empty-state-text">Y√ºkleniyor...</div>
              </div>
            ) : favorites.length === 0 ? (
              <div className="empty-state">
                <div className="empty-state-icon">üçΩÔ∏è</div>
                <div className="empty-state-text">Hen√ºz favori tarifiniz yok</div>
                <div className="empty-state-subtext">Ana sayfadan tarifleri ke≈üfedin ve favorilere ekleyin!</div>
              </div>
            ) : (
              <div className="favorites-grid">
                {favorites.map((fav, i) => (
                  <div key={i} className="favorite-card">
                    <div className="favorite-name">{fav.recipeName || 'Tarif Adƒ±'}</div>
                    <div className="favorite-category">{fav.category || 'Kategori'}</div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Yorumlar */}
          <div className="profile-section">
            <h3 className="section-title">
              <span className="section-title-icon">üí¨</span>
              Yaptƒ±ƒüƒ±nƒ±z Yorumlar
            </h3>
            {reviews.length === 0 ? (
              <div className="empty-state">
                <div className="empty-state-icon">‚≠ê</div>
                <div className="empty-state-text">Hen√ºz yorum yapmadƒ±nƒ±z</div>
                <div className="empty-state-subtext">Tarifleri deƒüerlendirip yorum yapmaya ba≈ülayƒ±n!</div>
              </div>
            ) : (
              <div className="reviews-list">
                {reviews.map((rev, i) => (
                  <div key={i} className="review-item">
                    <div className="review-recipe">{rev.recipeName || 'Tarif Adƒ±'}</div>
                    <div className="review-comment">{rev.comment || 'Yorum'}</div>
                    <div className="review-rating">‚≠ê {rev.rating || 0}/5</div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Profil D√ºzenleme Modal */}
      {showEditModal && (
        <ProfileEditModal
          user={user}
          userProfile={userProfile}
          onClose={() => {
            setShowEditModal(false);
            // Profil g√ºncellendikten sonra sayfayƒ± yenile
            window.location.reload();
          }}
        />
      )}
    </div>
  );
};

export default ProfilePage;
