import React, { useEffect, useMemo, useState } from "react";
import Navbar from "../components/Navbar";
import RecipeCard from "../components/Recipe/RecipeCard";
import RecipeDetailModal from "../components/Recipe/RecipeDetailModal";
import IngredientFilterBox from "../components/Shared/IngredientFilterBox";
import { getImageUrl } from "../services/api";
import { favoriteService } from "../services/favoriteService";
import axios from "axios";

function HomePage({ user, setUser, onLoginClick, onRegisterClick }) {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Tümü");
  const [categories, setCategories] = useState(["Tümü"]);
  const [userIngredients, setUserIngredients] = useState([]);
  const [inputIngredient, setInputIngredient] = useState("");
  const [showOnlyPossible, setShowOnlyPossible] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [favError, setFavError] = useState("");
  const [theme, setTheme] = useState("light");

  const filteredRecipes = useMemo(() => {
    return recipes.filter((r) => {
      if (!r) return false;
      const title = r.recipeName?.toLowerCase() || "";
      const category = r.category || "";
      const search = searchTerm.toLowerCase();
      const matchesSearch =
        title.includes(search) || category.toLowerCase().includes(search);
      const matchesCategory =
        selectedCategory === "Tümü" ||
        (category && category.trim().toLowerCase() === selectedCategory.trim().toLowerCase());

      let matchesIngredients = true;
      if (userIngredients.length > 0) {
        let recipeIngredients = [];
        if (typeof r.recipeIngredients === "string") {
          recipeIngredients = r.recipeIngredients
            .split(/,|\n/)
            .map((i) => i.trim().toLowerCase())
            .filter(Boolean);
        } else if (Array.isArray(r.recipeIngredients)) {
          recipeIngredients = r.recipeIngredients.map((i) => i.trim().toLowerCase());
        }
        matchesIngredients = userIngredients.some((userIng) =>
          recipeIngredients.some((recipeIng) => recipeIng.includes(userIng))
        );
      }
      return matchesSearch && matchesCategory && matchesIngredients;
    });
  }, [recipes, searchTerm, selectedCategory, userIngredients]);

  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    localStorage.setItem("theme", newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
  };

  useEffect(() => {
    loadRecipes();
  }, []);

  const loadRecipes = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
      const response = await axios.get(`${API_BASE_URL}/api/Recipe/GetRecipes`, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      const recipesData = response.data;
      const processedRecipes = recipesData.map((recipe) => ({
        ...recipe,
        recipeImageUrl: getImageUrl(recipe.recipeImageUrl),
      }));

      setRecipes(processedRecipes);

      const uniqueCategories = [
        ...new Set(
          recipesData.map((recipe) => recipe?.category).filter((category) => category && category.trim() !== "")
        ),
      ];
      setCategories(["Tümü", ...uniqueCategories]);
    } catch (error) {
      console.error("Tarifler API hatası:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user?.userId) loadUserFavorites();
  }, [user]);

  const loadUserFavorites = async () => {
    try {
      const response = await favoriteService.getUserFavorites(user.userId);
      const favoritesData = response.data;
      let favoriteIds = [];
      if (Array.isArray(favoritesData)) {
        if (favoritesData.length > 0 && typeof favoritesData[0] === "object" && favoritesData[0].recipeId) {
          favoriteIds = favoritesData.map((fav) => fav.recipeId);
        } else if (typeof favoritesData[0] === "number") {
          favoriteIds = favoritesData;
        }
      }
      setFavorites(favoriteIds);
    } catch (error) {
      setFavorites([]);
      console.error("Favoriler hatası:", error);
    }
  };

  const handleFavorite = async (recipeId) => {
    if (!user) return;
    setFavError("");
    try {
      const data = { userId: user.userId, recipeId };
      if (favorites.includes(recipeId)) {
        await favoriteService.removeFromFavorites(data);
        setFavorites(favorites.filter((id) => id !== recipeId));
      } else {
        await favoriteService.addToFavorites(data);
        setFavorites([...favorites, recipeId]);
      }
    } catch (error) {
      console.error("Favori hatası:", error);
      setFavError("Favori işlemi başarısız oldu. Lütfen tekrar deneyin.");
    }
  };

  return (
    <>
      <Navbar
        user={user}
        onProfileClick={() => window.location.assign("/profil")}
        onLogoClick={() => window.location.assign("/")}
        theme={theme}
        toggleTheme={toggleTheme}
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        categories={categories}
        onLoginClick={onLoginClick}
        onRegisterClick={onRegisterClick}
        onLogout={() => {
          localStorage.clear();
          setUser(null);
          setFavorites([]);
        }}
      />
      <IngredientFilterBox
        inputIngredient={inputIngredient}
        setInputIngredient={setInputIngredient}
        userIngredients={userIngredients}
        setUserIngredients={setUserIngredients}
        showOnlyPossible={showOnlyPossible}
        setShowOnlyPossible={setShowOnlyPossible}
      />

      {favError && <div style={{ color: "#e60023", textAlign: "center" }}>{favError}</div>}

      <div className="recipe-grid">
        {loading ? (
          <div style={{ textAlign: "center", padding: "50px" }}>Tarifler yükleniyor...</div>
        ) : (
          filteredRecipes.map((r) => (
            <RecipeCard
              key={r.recipeId}
              title={r.recipeName}
              imageUrl={r.recipeImageUrl}
              ingredients={r.recipeIngredients}
              recipeInstructions={r.recipeInstructions}
              userIngredients={userIngredients}
              onClick={() => setSelectedRecipe(r)}
              showIngredients={false}
              isFavorite={favorites.includes(r.recipeId)}
              onFavoriteClick={() => handleFavorite(r.recipeId)}
              user={user}
            />
          ))
        )}
      </div>

      {selectedRecipe && (
        <RecipeDetailModal
          recipe={selectedRecipe}
          user={user}
          onClose={() => setSelectedRecipe(null)}
        />
      )}
    </>
  );
}

export default HomePage;
