import React, { useEffect, useState } from "react";
import axios from "axios";
import "./RecipeDetailModal.css";
import RecipeReviewForm from "../RecipeReviewForm";

const RecipeDetailModal = ({ recipe, user, onClose }) => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";

  useEffect(() => {
    if (!recipe?.id) return;
    fetchReviews();
  }, [recipe?.id]);

  const fetchReviews = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/api/Review/GetReviewsByRecipeId/${recipe.id}`);
      setReviews(res.data || []);
    } catch (err) {
      console.error("Yorumlar yüklenemedi:", err);
      setError("Yorumlar yüklenemedi.");
    }
    setLoading(false);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>X</button>

        <h2>{recipe.title}</h2>
        <p>{recipe.description}</p>
        <p><strong>Hazırlık Süresi:</strong> {recipe.prep_time} dk</p>
        <p><strong>Kalori:</strong> {recipe.calories}</p>

        <h3>Yorumlar</h3>
        {loading ? (
          <p>Yorumlar yükleniyor...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          reviews.length > 0 ? (
            <ul className="review-list">
              {reviews.map((review) => (
                <li key={review.id}>
                  <strong>{review.username || "Kullanıcı"}:</strong> {review.comment} ({review.rating}/5)
                </li>
              ))}
            </ul>
          ) : (
            <p>Bu tarif için henüz yorum yapılmamış.</p>
          )
        )}

        {user && (
          <RecipeReviewForm
            recipeId={recipe.id}
            user={user}
            onReviewSubmit={() => {
              // Yeni yorum eklendiğinde yorumları tekrar yükle
              fetchReviews();
            }}
          />
        )}
      </div>
    </div>
  );
};

export default RecipeDetailModal;
