import axios from 'axios';

const API_BASE_URL = 'http://localhost:7175';

// Axios instance oluştur
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor - JWT token ekle
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('jwt');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor - Hata yönetimi
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('jwt');
      localStorage.removeItem('user');
      window.location.reload();
    }
    return Promise.reject(error);
  }
);

// Recipe API'leri
export const recipeAPI = {
  // Tüm tarifleri getir
  getRecipes: () => api.get('/api/Recipe/GetRecipes'),
  
  // Tarif detayını getir
  getRecipeById: (recipeId) => api.get(`/api/Recipe/GetRecipeById/${recipeId}`),
  
  // Tarif ara
  searchRecipes: (query) => api.get(`/api/Recipe/SearchRecipes?query=${query}`),
  
  // Kullanıcının tariflerini getir
  getRecipesByUserId: (userId) => api.get(`/api/Recipe/GetRecipesByUserId/${userId}`),
  
  // Yeni tarif oluştur
  createRecipe: (recipeData) => api.post('/api/Recipe/CreateRecipe', recipeData),
  
  // Tarif güncelle
  updateRecipe: (recipeData) => api.put('/api/Recipe/UpdateRecipe', recipeData),
  
  // Tarif sil
  deleteRecipe: (recipeId) => api.post(`/api/Recipe/DeleteRecipe/${recipeId}`),
};

// User API'leri
export const userAPI = {
  // Kullanıcı girişi
  login: (credentials) => api.post('/api/User/Login', credentials),
  
  // Kullanıcı kaydı
  register: (userData) => api.post('/api/User/Register', userData),
  
  // Kullanıcı bilgilerini getir
  getUserById: (userId) => api.get(`/api/User/GetUserById/${userId}`),
  
  // Kullanıcı güncelle
  updateUser: (userData) => api.put('/api/User/UpdateUser', userData),
  
  // Kullanıcı sil
  deleteUser: (userId) => api.post('/api/User/DeleteUser', { userId }),
};

// Favorite API'leri
export const favoriteAPI = {
  // Favorilere ekle
  addToFavorites: (data) => api.post('/api/Favorite/AddToFavorites', data),
  
  // Favorilerden çıkar
  removeFromFavorites: (data) => api.post('/api/Favorite/RemoveFromFavorites', data),
  
  // Kullanıcının favorilerini getir
  getUserFavorites: (userId) => api.get(`/api/Favorite/GetUserFavorites/${userId}`),
  
  // Favori olup olmadığını kontrol et
  isFavorite: (data) => api.get('/api/Favorite/IsFavorite', { params: data }),
  
  // Favori durumunu değiştir
  toggleFavorite: (data) => api.post('/api/Favorite/ToggleFavorite', data),
  
  // Kullanıcının favorilerinde ara
  searchUserFavorites: (query) => api.get('/api/Favorite/SearchUserFavorites', { params: { query } }),
};

// Review API'leri
export const reviewAPI = {
  // Tüm yorumları getir
  getReviews: () => api.get('/api/Review/GetReviews'),
  
  // Yorum detayını getir
  getReviewById: (reviewId) => api.get(`/api/Review/GetReviewById/${reviewId}`),
  
  // Yeni yorum oluştur
  createReview: (reviewData) => api.post('/api/Review/CreateReview', reviewData),
  
  // Yorum güncelle
  updateReview: (reviewData) => api.put('/api/Review/UpdateReview', reviewData),
  
  // Yorum sil
  deleteReview: (reviewId) => api.post(`/api/Review/DeleteReview/${reviewId}`),
};

export default api;
