import React, { useEffect, useState } from "react";
import axios from "axios";
import RecipeCard from "../components/Recipe/RecipeCard";

const HomePage = ({ user, searchTerm, selectedCategory }) => {
  const [recipes, setRecipes] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
  const token = localStorage.getItem("token");

  // 1. Tarifleri getir
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get(`${API_BASE_URL}/api/Recipe/GetRecipes`);
        setRecipes(res.data);
      } catch (err) {
        console.error("Tarifler yüklenemedi:", err);
      }
    };
    fetchRecipes();
  }, []);

  // 2. Favorileri getir
  useEffect(() => {
    const fetchFavorites = async () => {
      if (!user?.userId || !token) return;
      try {
        const res = await axios.get(`${API_BASE_URL}/api/Favorite/GetFavoritesByUserId/${user.userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFavorites(res.data);
      } catch (err) {
        console.error("Favoriler yüklenemedi:", err);
      }
    };
    fetchFavorites();
  }, [user, token]);

  // 3. Filtreleme useEffect içine alındı
  useEffect(() => {
    const filtered = recipes.filter((recipe) => {
      const nameMatch = recipe.recipeName.toLowerCase().includes(searchTerm.toLowerCase());
      const categoryMatch = selectedCategory ? recipe.category === selectedCategory : true;
      return nameMatch && categoryMatch;
    });
    setFilteredRecipes(filtered);
  }, [recipes, searchTerm, selectedCategory]);

  // 4. Favori işlemleri
  const addToFavorites = async (recipeId) => {
    try {
      await axios.post(
        `${API_BASE_URL}/api/Favorite/AddToFavorites`,
        { userId: user.userId, recipeId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFavorites([...favorites, { recipeId }]);
    } catch (err) {
      console.error("Favori eklenemedi:", err);
    }
  };

  const removeFromFavorites = async (recipeId) => {
    try {
      await axios.post(
        `${API_BASE_URL}/api/Favorite/RemoveFromFavorites`,
        { userId: user.userId, recipeId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFavorites(favorites.filter((fav) => fav.recipeId !== recipeId));
    } catch (err) {
      console.error("Favori silinemedi:", err);
    }
  };

  return (
    <div className="home-page">
      <RecipeList
  recipes={filteredRecipes}
  favorites={favorites}
  onAddFavorite={addToFavorites}
  onRemoveFavorite={removeFromFavorites}
/>

        const isFavorite = favorites.some((fav) => fav.recipeId === recipe.id);
        return (
          <RecipeCard
            key={recipe.id}
            recipe={recipe}
            isFavorite={isFavorite}
            onAddFavorite={() => addToFavorites(recipe.id)}
            onRemoveFavorite={() => removeFromFavorites(recipe.id)}
          />
        );
      })
    </div>
  );
};

export default HomePage;
