import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProfilePage.css";

const tabs = [
  { key: "profile", label: "Profil" },
  { key: "recipes", label: "Tarif Defteri" },
  { key: "comments", label: "Yorumlarım" },
];

export default function ProfilePage({ user, favorites, recipes }) {
  const [activeTab, setActiveTab] = useState("profile");
  const [userComments, setUserComments] = useState([]);
  const [loadingComments, setLoadingComments] = useState(true);

  const favoriteRecipes = recipes.filter(r => favorites.includes(r.id));

  // ✅ Yorumları çek (sadece bir kez ve kullanıcı varsa)
  useEffect(() => {
    if (!user) return;
    const jwt = localStorage.getItem("jwt");
    axios
      .get(
        `http://localhost:1337/api/reviews?filters[user][id][$eq]=${user.id}&populate=tarif`,
        { headers: { Authorization: `Bearer ${jwt}` } }
      )
      .then(res => {
        setUserComments(res.data.data || []);
        setLoadingComments(false);
      })
      .catch(() => setLoadingComments(false));
  }, [user]);

  console.log(userComments);
  return (
    <div className="profile-root">
      <div className="profile-cover">
        <button className="profile-photo-btn" onClick={() => alert("Fotoğraf ekleme yakında!")}>📷 Fotoğraf Ekle</button>
      </div>
      <div className="profile-main">
        <div className="profile-avatar">
          <span role="img" aria-label="avatar" style={{ fontSize: 64 }}>👤</span>
        </div>
        <div className="profile-info">
          <div className="profile-name">{user?.username || "Kullanıcı"}</div>
          <div className="profile-username">@{user?.username || "kullanici"}</div>
        </div>
        <button className="profile-edit-btn" onClick={() => alert("Profil düzenleme yakında!")}>Profili Düzenle</button>
      </div>

      <div className="profile-tabs">
        {tabs.map(tab => (
          <button
            key={tab.key}
            className={activeTab === tab.key ? "profile-tab active" : "profile-tab"}
            onClick={() => setActiveTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      <div className="profile-tab-content">
        {activeTab === "profile" && (
          <div>
            <h3>Hoş geldin, {user?.username || "Kullanıcı"}!</h3>
            <p>Burada profil bilgilerini ve ayarlarını görebilirsin.</p>
          </div>
        )}

        {activeTab === "recipes" && (
          <div>
            <h3>Favori Tariflerin</h3>
            <div className="recipe-grid">
              {favoriteRecipes.length === 0 ? (
                <div>Henüz favori tarifin yok.</div>
              ) : favoriteRecipes.map((r) => (
                <div key={r.id} className="profile-fav-card">
                  <img
                    src={
                      Array.isArray(r.image) && r.image.length > 0
                        ? "http://localhost:1337" + (r.image[0].formats?.medium?.url || r.image[0].url)
                        : ""
                    }
                    alt={r.title}
                    style={{ width: "100%", borderRadius: 8, marginBottom: 8 }}
                  />
                  <div style={{ fontWeight: "bold" }}>{r.title}</div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === "comments" && (
          <div>
            <h3>Yorumlarım</h3>
            {loadingComments ? (
              <p>Yorumlar yükleniyor...</p>
            ) : userComments.length === 0 ? (
              <p>Henüz yorum yapmadınız.</p>
            ) : (
              <ul style={{ listStyle: "none", paddingLeft: 0 }}>
                {userComments.map((comment) => (
                  <li key={comment.id} style={{ marginBottom: 12, borderBottom: "1px solid #ccc", paddingBottom: 8 }}>
                    <strong>{comment.attributes.tarif?.data?.attributes?.title || "Tarif silinmiş"}</strong>
                    <p>{comment.attributes.comment}</p>
                    <small>⭐ {comment.attributes.rating} / 5</small>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
