import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProfileEditModal.css";

const ProfileEditModal = ({ user, userProfile, onClose }) => {
  const [username, setUsername] = useState(user?.username || "");
  const [email, setEmail] = useState(user?.email || "");
  const [bio, setBio] = useState(userProfile?.bio || "");
  const [profileImageBase64, setProfileImageBase64] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState("");
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";
  const token = localStorage.getItem("token");

  // Mevcut profil fotoƒürafƒ±nƒ± g√∂ster
  useEffect(() => {
    if (userProfile?.profileImageBase64) {
      setProfileImageBase64(userProfile.profileImageBase64);
    }
  }, [userProfile]);

  const validateForm = () => {
    const newErrors = {};

    if (!username.trim()) {
      newErrors.username = "Kullanƒ±cƒ± adƒ± gereklidir";
    } else if (username.length < 3) {
      newErrors.username = "Kullanƒ±cƒ± adƒ± en az 3 karakter olmalƒ±dƒ±r";
    }

    if (!email.trim()) {
      newErrors.email = "E-posta gereklidir";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = "Ge√ßerli bir e-posta adresi giriniz";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Dosya boyutu kontrol√º (5MB)
    if (file.size > 5 * 1024 * 1024) {
      setMessage("Dosya boyutu 5MB'dan k√º√ß√ºk olmalƒ±dƒ±r");
      setMessageType("error");
      return;
    }

    // Dosya tipi kontrol√º
    if (!file.type.startsWith('image/')) {
      setMessage("Sadece resim dosyalarƒ± kabul edilir");
      setMessageType("error");
      return;
    }

    setSelectedFile(file);

    const reader = new FileReader();
    reader.onloadend = () => {
      // Backend data:image/...;base64, formatƒ±nƒ± bekliyor
      setProfileImageBase64(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    setMessage("");

    try {
      // √ñnce profil bilgilerini g√ºncelle
      const updateData = {
        userId: user.userId,
        username: username.trim(),
        email: email.trim(),
        bio: bio.trim(),
        profileImageBase64: null // Fotoƒürafƒ± ayrƒ± y√ºkle
      };

      const res = await axios.post(
        `${API_BASE_URL}/api/UserProfile/UpdateUserProfile`,
        updateData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Eƒüer yeni fotoƒüraf se√ßildiyse, ayrƒ±ca fotoƒüraf y√ºkle
      if (profileImageBase64 && profileImageBase64 !== userProfile?.profileImageBase64) {
        console.log("Yeni fotoƒüraf y√ºkleniyor...");
        console.log("Se√ßilen fotoƒüraf:", profileImageBase64.substring(0, 100) + "...");

        const photoData = {
          userId: user.userId,
          profileImageBase64: profileImageBase64
        };

        const photoRes = await axios.post(
          `${API_BASE_URL}/api/UserProfile/UploadUserProfilePhoto`,
          photoData,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("Fotoƒüraf y√ºkleme sonucu:", photoRes.data);
      }

      setMessage("Profil ba≈üarƒ±yla g√ºncellendi! üéâ");
      setMessageType("success");

      // LocalStorage'daki user bilgisini g√ºncelle
      const currentUser = JSON.parse(localStorage.getItem("user") || "{}");
      const updatedUser = { ...currentUser, username: username.trim(), email: email.trim() };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      // Profil bilgilerini yenile
      setTimeout(() => {
        window.location.reload(); // Sayfayƒ± yenile
      }, 2000);
    } catch (err) {
      console.error("Profil g√ºncelleme hatasƒ±:", err);

      if (err.response?.status === 409) {
        setMessage("Bu kullanƒ±cƒ± adƒ± veya e-posta zaten kullanƒ±lƒ±yor");
      } else if (err.response?.status === 400) {
        setMessage("Ge√ßersiz veri formatƒ±");
      } else {
        setMessage("Profil g√ºncellenirken bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
      }
      setMessageType("error");
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    if (loading) return; // Y√ºkleme sƒ±rasƒ±nda kapatmayƒ± engelle
    onClose();
  };

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Kapatma Butonu */}
        <button className="close-btn" onClick={handleClose} disabled={loading}>
          ‚úï
        </button>

        {/* Modal Ba≈ülƒ±ƒüƒ± */}
        <div className="modal-header">
          <h3 className="modal-title">Profil Bilgilerini G√ºncelle</h3>
          <p className="modal-subtitle">Ki≈üisel bilgilerinizi d√ºzenleyin</p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit}>
          {/* Kullanƒ±cƒ± Adƒ± */}
          <div className="form-group">
            <label className="form-label">üë§ Kullanƒ±cƒ± Adƒ±</label>
            <input
              type="text"
              className={`form-input ${errors.username ? 'error' : ''}`}
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Kullanƒ±cƒ± adƒ±nƒ±zƒ± girin"
              disabled={loading}
            />
            {errors.username && <span className="error-text">{errors.username}</span>}
          </div>

          {/* E-posta */}
          <div className="form-group">
            <label className="form-label">üìß E-posta</label>
            <input
              type="email"
              className={`form-input ${errors.email ? 'error' : ''}`}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="E-posta adresinizi girin"
              disabled={loading}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          {/* Hakkƒ±mda */}
          <div className="form-group">
            <label className="form-label">üìù Hakkƒ±mda</label>
            <textarea
              className="form-input"
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="Kendiniz hakkƒ±nda kƒ±sa bir a√ßƒ±klama yazƒ±n (opsiyonel)"
              rows="3"
              disabled={loading}
            />
          </div>

          {/* Profil Fotoƒürafƒ± */}
          <div className="form-group">
            <label className="form-label">üì∏ Profil Fotoƒürafƒ±</label>
            <div className="file-input-container">
              <input
                type="file"
                id="profile-image"
                className="file-input"
                accept="image/*"
                onChange={handleFileChange}
                disabled={loading}
              />
              <label 
                htmlFor="profile-image" 
                className={`file-input-label ${selectedFile ? 'has-file' : ''}`}
              >
                {selectedFile ? 'üìÅ Dosya Se√ßildi' : 'üìÅ Fotoƒüraf Se√ß'}
              </label>
            </div>

            {selectedFile && (
              <div className="file-name">
                Se√ßilen dosya: {selectedFile.name}
              </div>
            )}

            {/* √ñnizleme */}
            {profileImageBase64 && (
              <img
                src={profileImageBase64.startsWith('data:') 
                  ? profileImageBase64 
                  : `${API_BASE_URL}${profileImageBase64}`
                }
                alt="Profil √∂nizleme"
                className="preview-image"
                onError={(e) => {
                  console.log("√ñnizleme fotoƒürafƒ± y√ºklenemedi");
                  e.target.style.display = 'none';
                }}
              />
            )}
          </div>

          {/* Butonlar */}
          <div className="modal-actions">
            <button 
              type="button" 
              className="btn btn-outline" 
              onClick={handleClose}
              disabled={loading}
            >
              ‚ùå ƒ∞ptal
            </button>
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? '‚è≥ Kaydediliyor...' : 'üíæ Kaydet'}
            </button>
          </div>
        </form>

        {/* Durum Mesajƒ± */}
        {message && (
          <div className={`status-message ${messageType}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileEditModal;
