import React, { useState, useEffect } from "react";
import axios from "axios";
import ProfileEditModal from "../components/Profile/ProfileEditModal";
import "./ProfilePage.css";

const ProfilePage = ({ user }) => {
  const [profile, setProfile] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [showPhotoUpload, setShowPhotoUpload] = useState(false);

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";

  const fetchUserProfile = async () => {
    if (!user?.userId) return;
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE_URL}/api/User/GetUserProfile/${user.userId}`);
      setProfile(res.data);
    } catch (error) {
      console.error("Profil yüklenemedi:", error);
      setProfile(null);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchUserProfile();
  }, [user?.userId]);

  if (loading) return <div>Yükleniyor...</div>;
  if (!profile) return <div>Profil bulunamadı.</div>;

  return (
    <div className="profile-page">
      <h2>Profilim</h2>
      <p><strong>Kullanıcı Adı:</strong> {user.username}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Bio:</strong> {profile.bio || "Yok"}</p>
      <p><strong>Konum:</strong> {profile.location || "Belirtilmemiş"}</p>

      <button onClick={() => setShowEditModal(true)}>Profili Düzenle</button>

      {showEditModal && (
        <ProfileEditModal
          user={user}
          onClose={() => setShowEditModal(false)}
          onSave={fetchUserProfile}
        />
        )}
        {showPhotoUpload && (
        <ProfilePhotoUpload
            user={user}
            onUploadSuccess={fetchUserProfile}
            /> 
        )}   
    </div>
  );
};

export default ProfilePage;
