import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./components/Navbar";
import RecipeCard from "./components/RecipeCard";
import "./App.css";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("Tümü");
  const [categories, setCategories] = useState(["Tümü"]);

  useEffect(() => {
    axios
      .get("http://localhost:1337/api/tarifs?populate=*")
      .then((res) => {
        setRecipes(res.data.data);
        console.log("Gelen tarifler:", res.data.data);

        // Veritabanından gelen kategorileri al
        const uniqueCategories = [...new Set(
          res.data.data
            .map(recipe => recipe.attributes.category)
            .filter(category => category && category.trim() !== "")
        )];

        setCategories(["Tümü", ...uniqueCategories]);
        console.log("Bulunan kategoriler:", uniqueCategories);
      })
      .catch((err) => console.error("Veri alınamadı:", err));
  }, []);

  return (
    <div>
      <Navbar
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        categories={categories}
      />
      <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
        <div className="recipe-grid">
        {recipes
  .filter((r) => {
    if (!r) return false;
    const title = r.title?.toLowerCase() || "";
    const matchesSearch = title.includes(searchTerm.toLowerCase());
    const category = r.category || "";
    const matchesCategory =
      selectedCategory === "Tümü" ||
      (category && category.trim().toLowerCase() === selectedCategory.trim().toLowerCase());
    return matchesSearch && matchesCategory;
  })
  .map((r) => {
    const { id, title, image, calories, ingredients, prep_time, is_vegetarian } = r;
    // Görsel url'si için:
    let imageUrl = "";
    if (Array.isArray(image) && image.length > 0) {
      // Küçük, orta, büyük formatı varsa kullan, yoksa ana url
      imageUrl = "http://localhost:1337" + (image[0].formats?.medium?.url || image[0].url);
    }
    return (
      <RecipeCard
        key={id}
        title={title}
        imageUrl={imageUrl}
        calories={calories}
        ingredients={ingredients}
        prepTime={prep_time}
        isVegetarian={is_vegetarian}
      />
    );
  })
}
        </div>
      </div>
    </div>
  );
}

export default App;
