import React, { useState, useRef, useEffect } from "react";
import { userService } from "../services/userService";
import { getProfilePhotoUrl } from "../services/api";
import "./Navbar.css";

const Navbar = ({ searchTerm, setSearchTerm, selectedCategory, setSelectedCategory, categories, onLoginClick, onRegisterClick, user, onLogout, onProfileClick, onLogoClick, theme, toggleTheme }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [userProfile, setUserProfile] = useState(null);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const menuRef = useRef();

  useEffect(() => {
    function handleClick(e) {
      if (menuRef.current && !menuRef.current.contains(e.target)) setMenuOpen(false);
    }
    if (menuOpen) document.addEventListener("mousedown", handleClick);
    return () => document.removeEventListener("mousedown", handleClick);
  }, [menuOpen]);

  // Kullanƒ±cƒ± profili y√ºkle
  useEffect(() => {
    if (user?.userId) {
      // √ñnceki profil verilerini temizle
      setUserProfile(null);
      setProfilePhoto(null);
      
      // Kƒ±sa bir gecikme ile yeni profil y√ºkle
      setTimeout(() => {
        loadUserProfile();
      }, 50);
    } else {
      // Kullanƒ±cƒ± yoksa profil bilgilerini temizle
      setUserProfile(null);
      setProfilePhoto(null);
    }
  }, [user?.userId, user?.username]); // Hem userId hem de username deƒüi≈üikliklerini izle

  // Profil fotoƒürafƒ±nƒ± ayarla
  useEffect(() => {
    if (userProfile?.profileImageBase64) {
      // Base64 formatƒ±nda ise doƒürudan kullan
      if (userProfile.profileImageBase64.startsWith('data:image/')) {
        setProfilePhoto(userProfile.profileImageBase64);
      } else {
        // Dosya yolu formatƒ±nda ise URL olu≈ütur
        const photoUrl = getProfilePhotoUrl(userProfile.profileImageBase64);
        // Kullanƒ±cƒ± ID'si ile cache busting yap
        const cacheBustUrl = `${photoUrl}?userId=${user?.userId}&t=${Date.now()}`;
        setProfilePhoto(cacheBustUrl);
      }
    } else {
      // Profil fotoƒürafƒ± yoksa temizle
      setProfilePhoto(null);
    }
  }, [userProfile, user?.userId]);

  const loadUserProfile = async () => {
    try {
      // Kullanƒ±cƒ± kontrol√º
      if (!user?.userId) {
        console.log("Navbar - Kullanƒ±cƒ± ID yok, profil y√ºkleme iptal edildi");
        return;
      }
      
      console.log("Navbar - Profil y√ºkleniyor, userId:", user.userId);
      
      // Profil var mƒ± kontrol et
      const existsResponse = await userService.profileExists(user.userId);
      
      if (existsResponse.data) {
        // Profil varsa getir
        const response = await userService.getUserProfile(user.userId);
        if (response.data) {
          console.log("Navbar - Profil y√ºklendi:", response.data);
          setUserProfile(response.data);
        }
      } else {
        console.log("Navbar - Profil bulunamadƒ±, userId:", user.userId);
        setUserProfile(null);
      }
    } catch (error) {
      console.error("Navbar - Profil y√ºkleme hatasƒ±:", error);
      setUserProfile(null);
    }
  };

  console.log("Navbar kategorileri:", categories);
  console.log("Navbar user:", user);

  return (
    <nav className="navbar">
      <div style={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div className="logo-area" onClick={onLogoClick} style={{ cursor: "pointer", display: "flex", alignItems: "center", gap: 8 }}>
          <span className="logo-icon">üç≤</span>
      <h2 className="logo">Tarif Kapƒ±da</h2>
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <button className="theme-toggle-btn" onClick={toggleTheme}>
            {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
          </button>
          {user && user.username ? (
            <div style={{ position: "relative" }} ref={menuRef}>
              <button className="nav-profile-btn" onClick={() => setMenuOpen(v => !v)}>
                {profilePhoto ? (
                  <img 
                    src={`${profilePhoto}&cb=${Date.now()}`} 
                    alt="Profil fotoƒürafƒ±" 
                    className="nav-profile-photo"
                    key={`${user?.userId}-${Date.now()}`}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'inline';
                    }}
                  />
                ) : null}
                <span className="nav-profile-icon" style={{ display: profilePhoto ? 'none' : 'inline' }}>üë§</span>
                {user.username || user.email}
              </button>
              {menuOpen && (
                <div className="nav-profile-menu">
                  <button className="nav-profile-menu-item" onClick={onProfileClick}>Profilim</button>
                  <button className="nav-profile-menu-item">≈ûifre Deƒüi≈ütir</button>
                  <button className="nav-profile-menu-item" onClick={onLogout}>√áƒ±kƒ±≈ü</button>
                </div>
              )}
            </div>
          ) : (
            <div style={{ display: "flex", gap: 8 }}>
              <button className="nav-auth-btn" onClick={onLoginClick}>Giri≈ü Yap</button>
              <button className="nav-auth-btn" onClick={onRegisterClick}>√úye Ol</button>
            </div>
          )}
        </div>
      </div>
      <input
        type="text"
        placeholder="Tarif ara..."
        className="search-input"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div className="nav-links">
        {categories.map((cat) => (
          <button
            key={cat}
            className={selectedCategory === cat ? "active-category" : ""}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
