import React from "react";
import RecipeCard from "../components/Recipe/RecipeCard";

function ProfilePage({ user, favorites, recipes, onFavoriteClick }) {
  if (!user) {
    return <div style={{ padding: 32, textAlign: "center" }}>Lütfen giriş yapın.</div>;
  }

  const favoriteRecipes = recipes.filter((recipe) => favorites.includes(recipe.recipeId));

  return (
    <div style={{ padding: "32px", maxWidth: "1200px", margin: "0 auto" }}>
      <h2 style={{ marginBottom: 24 }}>Profilim</h2>

      <div
        style={{
          background: "var(--card-bg)",
          padding: 16,
          borderRadius: 12,
          marginBottom: 32,
          boxShadow: "0 2px 8px var(--shadow-color)",
        }}
      >
        <p><strong>Kullanıcı Adı:</strong> {user.username}</p>
        <p><strong>E-posta:</strong> {user.email}</p>
      </div>

      <h3>Favori Tariflerim</h3>
      {favoriteRecipes.length === 0 ? (
        <p>Henüz favori tarifiniz yok.</p>
      ) : (
        <div className="recipe-grid">
          {favoriteRecipes.map((recipe) => (
            <RecipeCard
              key={recipe.recipeId}
              title={recipe.recipeName}
              imageUrl={recipe.recipeImageUrl}
              ingredients={recipe.recipeIngredients}
              recipeInstructions={recipe.recipeInstructions}
              userIngredients={[]}
              showIngredients={false}
              onClick={() => {}} // Profilde tıklanabilirlik yok (isteğe bağlı eklenebilir)
              isFavorite={true}
              onFavoriteClick={() => onFavoriteClick(recipe.recipeId)}
              user={user}
            />
          ))}
        </div>
        
      )}
    </div>
  );
}

export default ProfilePage;
