import React, { useState } from "react";
import axios from "axios";
import StarRating from "./StarRating";

export default function RecipeReviewForm({
  recipeId,
  userI,
  onReviewAdded,
  rating,
  onRatingChange,
  comment,
  onCommentChange,
}) {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess(false);

    if (!rating || !comment.trim()) {
      setError("Lütfen puan ve yorum girin.");
      return;
    }

    setLoading(true);

    try {
      const jwt = localStorage.getItem("jwt");

      // ⚠️ user ve tarif alanlarını doğru ilişkilendirdik
      await axios.post(
        "http://localhost:1337/api/reviews",
        {
          data: {
            rating,
            comment,
            users_permissions: user.id,     // <== Strapi'de \"Review\" modelinde \"user\" alanı many-to-one olmalı!
            tarif: recipeId    // <== Aynı şekilde \"tarif\" alanı da many-to-one ilişki olmalı!
          }
        },
        {
          headers: {
            Authorization: `Bearer ${jwt}`,
          },
        }
      );
      

      setSuccess(true);
      onRatingChange(0);
      onCommentChange("");

      if (onReviewAdded) onReviewAdded();
    } catch (err) {
      console.error("Yorum gönderme hatası:", err);
      setError("Yorum kaydedilemedi. Lütfen tekrar deneyin.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 16 }}>
      <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 8 }}>
        <StarRating value={rating} onChange={onRatingChange} />
        <span style={{ color: "var(--text-secondary)", fontSize: 15 }}>
          {rating > 0 ? `${rating} / 5` : "Puan ver"}
        </span>
      </div>
      <textarea
        value={comment}
        onChange={(e) => onCommentChange(e.target.value)}
        placeholder="Yorumunuzu yazın..."
        rows={3}
        style={{
          width: "100%",
          borderRadius: 8,
          border: "1px solid var(--border-color)",
          padding: 8,
          fontSize: 15,
          background: "var(--input-bg)",
          color: "var(--text-primary)",
          marginBottom: 8,
        }}
      />
      {error && <div style={{ color: "var(--error-color)", marginBottom: 8 }}>{error}</div>}
      {success && <div style={{ color: "var(--success-color)", marginBottom: 8 }}>Yorumunuz kaydedildi!</div>}
      <button
        type="submit"
        disabled={loading}
        style={{
          background: "var(--accent-color)",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          padding: "8px 20px",
          fontWeight: 500,
          fontSize: 16,
          cursor: loading ? "not-allowed" : "pointer",
        }}
      >
        {loading ? "Gönderiliyor..." : "Gönder"}
      </button>
    </form>
  );
}
