import React, { useState } from "react";
import axios from "axios";
import "./RegisterForm.css";

const RegisterForm = ({ onRegisterSuccess, onClose }) => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post(`${API_BASE_URL}/api/User/Register`, {
        username,
        email,
        password,
      });

      const user = response.data;
      localStorage.setItem("user", JSON.stringify(user));
      localStorage.setItem("token", user.token || "");

      onRegisterSuccess(user);
      if (onClose) onClose(); // Modal kapatma
    } catch (err) {
      console.error("Kayıt hatası:", err);
      setError("Kayıt başarısız. Kullanıcı adı veya e-posta zaten kullanılıyor olabilir.");
    }
  };

  return (
    <div className="register-form">
      <h2>Üye Ol</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Kullanıcı adı"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="E-posta"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        {error && <div className="error-message">{error}</div>}
        <button type="submit">Kayıt Ol</button>
      </form>
    </div>
  );
};

export default RegisterForm;
