import React, { useState } from "react";
import axios from "axios";

function LoginForm({ onSuccess, onClose }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const API_BASE_URL = localStorage.getItem("API_BASE_URL") || "http://localhost:5043";

      const response = await axios.post(`${API_BASE_URL}/api/User/Login`, {
        username: email,
        password: password,
      });

      if (response.status === 204 || !response.data) {
        const userResponse = await axios.get(`${API_BASE_URL}/api/User/GetUserByEmail/${email}`);
        if (userResponse.data) {
          const user = userResponse.data;
          const userData = {
            userId: user.userId || user.id,
            username: user.username || user.email,
            email: user.email,
          };
          const tempToken = "temp_token_" + Date.now();
          localStorage.setItem("jwt", tempToken);
          localStorage.setItem("token", tempToken);
          if (onSuccess) onSuccess(userData);
          return;
        }
        setError("Kullanıcı bilgileri alınamadı.");
        return;
      }

      const { token, user } = response.data;
      if (!user) {
        setError("Kullanıcı bilgileri alınamadı.");
        return;
      }

      localStorage.setItem("jwt", token);
      localStorage.setItem("token", token);

      const userData = {
        userId: user.userId || user.id,
        username: user.username || user.email,
        email: user.email,
      };

      if (onSuccess) onSuccess(userData);
    } catch (err) {
      let errorMessage = "Giriş başarısız. Lütfen bilgilerinizi kontrol edin.";
      if (err.response?.status === 401) errorMessage = "Kullanıcı adı veya şifre hatalı.";
      else if (err.response?.status === 404) errorMessage = "Kullanıcı bulunamadı.";
      else if (err.response?.status === 500) errorMessage = "Sunucu hatası. Daha sonra tekrar deneyin.";
      else if (err.response?.data?.message) errorMessage = err.response.data.message;
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        background: "rgba(0,0,0,0.4)",
        zIndex: 2000,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
      onClick={onClose}
    >
      <div
        style={{
          background: "var(--modal-bg)",
          color: "var(--text-primary)",
          borderRadius: 16,
          padding: 32,
          maxWidth: 400,
          width: "100%",
          position: "relative",
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          style={{
            position: "absolute",
            top: 16,
            right: 16,
            background: "var(--accent-color)",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            padding: "4px 12px",
            fontSize: 18,
            cursor: "pointer",
          }}
        >
          Kapat
        </button>
        <h2 style={{ marginBottom: 24 }}>Giriş Yap</h2>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: 16 }}>
            <label htmlFor="login-email">E-posta</label>
            <input
              id="login-email"
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{ width: "100%", padding: 8, borderRadius: 6 }}
            />
          </div>
          <div style={{ marginBottom: 16 }}>
            <label htmlFor="login-password">Şifre</label>
            <input
              id="login-password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ width: "100%", padding: 8, borderRadius: 6 }}
            />
          </div>
          {error && <div style={{ color: "red", marginBottom: 12 }}>{error}</div>}
          <button
            type="submit"
            disabled={!email || !password || loading}
            style={{ width: "100%", padding: 10, borderRadius: 8, background: "var(--accent-color)", color: "#fff" }}
          >
            {loading ? "Giriş Yapılıyor..." : "Giriş Yap"}
          </button>
        </form>
      </div>
    </div>
  );
}

export default LoginForm;
