// Validasyonlu LoginForm ve RegisterForm bileşenleri:
function LoginForm({ onSuccess }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});
  const [serverError, setServerError] = useState("");
  const [loading, setLoading] = useState(false);

  function validateEmail(val) {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val);
  }
  function validatePassword(val) {
    return val.length >= 8 && /[A-Z]/.test(val) && /[a-z]/.test(val) && /[0-9]/.test(val) && /[^A-Za-z0-9]/.test(val);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setServerError("");
    const newErrors = {};
    if (!validateEmail(email)) newErrors.email = "Geçerli bir e-posta girin.";
    if (!validatePassword(password)) newErrors.password = "Şifre en az 8 karakter, büyük/küçük harf, rakam ve özel karakter içermeli.";
    setErrors(newErrors);
    if (Object.keys(newErrors).length === 0) {
      setLoading(true);
      try {
        const res = await axios.post("http://localhost:1337/api/auth/local", {
          identifier: email,
          password
        });
        localStorage.setItem("jwt", res.data.jwt);
        if (onSuccess) onSuccess();
      } catch (err) {
        setServerError(err?.response?.data?.error?.message || "Bir hata oluştu.");
      } finally {
        setLoading(false);
      }
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="login-email">E-posta</label>
        <input id="login-email" name="email" type="email" value={email} onChange={e => setEmail(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
        {errors.email && <div style={{ color: '#e60023', fontSize: 13 }}>{errors.email}</div>}
      </div>
      <div style={{ marginBottom: 16 }}>
        <label htmlFor="login-password">Şifre</label>
        <input id="login-password" name="password" type="password" value={password} onChange={e => setPassword(e.target.value)} required style={{ width: '100%', padding: 8, borderRadius: 6, border: '1px solid #ccc', marginTop: 4 }} />
        {errors.password && <div style={{ color: '#e60023', fontSize: 13 }}>{errors.password}</div>}
      </div>
      {serverError && <div style={{ color: '#e60023', fontSize: 14, marginBottom: 12 }}>{serverError}</div>}
      <button type="submit" style={{ width: '100%', padding: 10, borderRadius: 8, background: '#e60023', color: '#fff', border: 'none', fontWeight: 'bold', fontSize: 16 }} disabled={Object.keys(errors).length > 0 || !email || !password || loading}>{loading ? "Giriş Yapılıyor..." : "Giriş Yap"}</button>
    </form>
  );
}

function RegisterForm({ onSuccess }) {
