import React, { useState, useRef } from 'react';
import { userService } from '../../services/userService';
import { pickProfileImage } from '../../utils/imageUtils';
import './ProfilePhotoUpload.css';

const ProfilePhotoUpload = ({ user, userProfile, onPhotoUpdate }) => {
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState('');
  const [previewUrl, setPreviewUrl] = useState(null);
  const [base64Image, setBase64Image] = useState(null);
  const fileInputRef = useRef(null);



  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Dosya boyutu kontrolü (5MB)
    if (file.size > 5 * 1024 * 1024) {
      setError('Dosya boyutu 5MB\'dan küçük olmalıdır.');
      return;
    }

    // Dosya tipi kontrolü
    if (!file.type.startsWith('image/')) {
      setError('Lütfen geçerli bir resim dosyası seçin.');
      return;
    }

    setError('');
    
    // Önizleme için URL oluştur ve base64'e çevir
    const reader = new FileReader();
    reader.onload = (e) => {
      const base64 = e.target.result;
      setPreviewUrl(base64);
      setBase64Image(base64); // Backend'e gönderilecek base64 veri
    };
    reader.readAsDataURL(file);
  };

  // imageUtils kullanarak resim seçme
  const handlePickImage = async () => {
    try {
      const result = await pickProfileImage({
        maxSize: 5 * 1024 * 1024, // 5MB
        quality: 0.3, // Kalite
        aspect: [1, 1] // 1:1 oranı
      });
      
      if (result) {
        setPreviewUrl(result.uri);
        setBase64Image(result.base64);
        setError('');
      }
    } catch (error) {
      console.error('Resim seçme hatası:', error);
      setError('Resim seçilirken bir hata oluştu.');
    }
  };

  const handleUpload = async () => {
    if (!base64Image) {
      setError('Lütfen bir dosya seçin.');
      return;
    }

    if (!user?.userId) {
      setError('Kullanıcı bilgileri bulunamadı.');
      return;
    }
    
    try {
      setIsUploading(true);
      setError('');
      
      console.log('Profil fotoğrafı yükleniyor...', { userId: user.userId });
      
      // Backend kodlarınıza göre base64 resim gönder
      const response = await userService.uploadProfilePhotoBase64(user.userId, base64Image);
      
      console.log('Upload response:', response);
      
      // Backend'den ProfileImageUrl olarak geliyor
      if (response.data && response.data.ProfileImageUrl) {
        console.log('Fotoğraf başarıyla yüklendi:', response.data.ProfileImageUrl);
        
        // Başarılı yükleme sonrası callback
        if (onPhotoUpdate) {
          onPhotoUpdate(response.data.ProfileImageUrl);
        }
        
        // Formu temizle
        setPreviewUrl(null);
        setBase64Image(null);
        if (fileInputRef.current) {
          fileInputRef.current.value = '';
        }
        
        alert('Profil fotoğrafı başarıyla yüklendi!');
      } else {
        console.error('Response data eksik:', response.data);
        setError('Fotoğraf yüklenirken bir hata oluştu. Response data eksik.');
      }
    } catch (error) {
      console.error('Fotoğraf yükleme hatası:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      
      let errorMessage = 'Fotoğraf yüklenirken bir hata oluştu. Lütfen tekrar deneyin.';
      
      if (error.response?.data) {
        if (typeof error.response.data === 'string') {
          errorMessage = error.response.data;
        } else if (error.response.data.message) {
          errorMessage = error.response.data.message;
        }
      }
      
      setError(errorMessage);
    } finally {
      setIsUploading(false);
    }
  };

  const handleCancel = () => {
    setPreviewUrl(null);
    setBase64Image(null);
    setError('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="profile-photo-upload">
      <h3>Profil Fotoğrafı Yükle</h3>
      
      <div className="upload-section">
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          className="file-input"
          id="profile-photo-input"
        />
        <label htmlFor="profile-photo-input" className="file-input-label">
          📷 Fotoğraf Seç
        </label>
        
        {/* Alternatif resim seçme butonu */}
        <button 
          type="button"
          onClick={handlePickImage}
          className="pick-image-btn"
        >
          📷 Resim Seç (Gelişmiş)
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {previewUrl && (
        <div className="preview-section">
          <h4>Önizleme:</h4>
          <img src={previewUrl} alt="Profil fotoğrafı önizleme" className="preview-image" />
          <div className="preview-actions">
            <button 
              onClick={handleUpload} 
              disabled={isUploading}
              className="upload-btn"
            >
              {isUploading ? 'Yükleniyor...' : 'Yükle'}
            </button>
            <button onClick={handleCancel} className="cancel-btn">
              İptal
            </button>
          </div>
        </div>
      )}

      <div className="upload-info">
        <p><strong>Desteklenen formatlar:</strong> JPG, PNG, GIF</p>
        <p><strong>Maksimum dosya boyutu:</strong> 5MB</p>
      </div>
    </div>
  );
};

export default ProfilePhotoUpload;
